// Based on: https://bartwronski.com/2017/04/13/cull-that-cone/
// Has similar fail states as the final solution in the linked site.
// A more robust test could be implemented by finding the closest
// point to the sphere on the cylinder and
// testing the distance from that point to the center of the sphere.
// http://shader-playground.timjones.io/0c8f28cf32450dcbdc5ffd178b06fb16
bool cylinderSphereTest_imperfect(vec2 cylinderOrg, vec2 cylinderDir, float cylinderHeight, float cylinderRad, vec2 sphereOrg, float sphereRad)
{
    vec2 v = sphereOrg-cylinderOrg;
    float a = dot(cylinderDir, v);
    float bSqr = dot(v,v) - a*a;

    bool frontCull = a > cylinderHeight + sphereRad;
    bool backCull = a < -sphereRad;
    bool sideCull = bSqr > (sphereRad + cylinderRad) * (sphereRad + cylinderRad);

    return !(frontCull || backCull || sideCull);
}

bool cylinderSphereTest(vec2 cylinderOrg, vec2 cylinderDir, float cylinderHeight, float cylinderRad, vec2 sphereOrg, float sphereRad)
{
    vec2 v = sphereOrg-cylinderOrg;

    float da = dot(cylinderDir,v);
    vec2 a = cylinderDir * clamp(da,0.0,cylinderHeight);

    vec2 b = v - (cylinderDir * da);
    float bl = length(b);
    b = b/bl * min(bl,cylinderRad);

    vec2 s = cylinderOrg + a + b;
    float distSqr = dot(s-sphereOrg,s-sphereOrg);

    return distSqr < sphereRad * sphereRad;
}

bool circle(vec2 p, vec2 c, float r)
{
    return dot(p-c,p-c)<r*r;
}

bool cylinder(vec2 p, vec2 o, vec2 d, float h, float r)
{
    float a=dot(p-o,d);
    vec2 pr=clamp(a,0.0,h)*d+o;
    return (dot(pr-p,pr-p)<(r*r))&&a<h&&a>0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cylinderOrg = vec2(cos(iTime),sin(iTime))*0.5+0.6;
    vec2 cylinderDir = vec2(-cos(iTime),-sin(iTime));
    float cylinderHeight = 0.5;
    float cylinderRad = 0.1;

    vec2 sphereOrg = vec2(0.1,0.0);
    float sphereRad = 0.3;

    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;

    if(cylinder(uv,cylinderOrg,cylinderDir,cylinderHeight,cylinderRad))
    {
        fragColor=vec4(1.0);
    }

    if(circle(uv,sphereOrg,sphereRad))
    {
        bool intersect=cylinderSphereTest(cylinderOrg,cylinderDir,cylinderHeight,cylinderRad,sphereOrg,sphereRad);
        bool intersect_imperfect=cylinderSphereTest_imperfect(cylinderOrg,cylinderDir,cylinderHeight,cylinderRad,sphereOrg,sphereRad);

        fragColor = intersect?vec4(0.0,1.0,0.0,1.0):vec4(0.0,0.0,0.0,1.0);
		fragColor += intersect_imperfect?vec4(1.0,0.0,0.0,1.0):vec4(1.0,1.0,1.0,1.0);
    }
}

