}
///  Dave hoskins hash
vec3 r33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}
float hash( float n ) { return fract(sin(n)*753.5453123); }


#define rot(x) mat2(cos(x), -sin(x), sin(x),  cos(x))
#define pmod(p, x) mod(p,x) - x*0.5
float sdBlocksA(vec3 p, float scale) {

    float modDist = 26.*scale;
    vec3 r = r33(floor(p/modDist) + 20.2) - 0.5;
    vec3 rSign = sign(r);

    //r = smoothstep(0.,1., r*3.);
    //r *= rSign;
    vec3 rOrig = r;
    r = sign(r)*pow(clamp(abs(r)*80.5, 0. ,1.), vec3(20.));
	float scaleO = scale;    

    scale *= 1. + abs(rOrig.x)*3.;
    p = pmod(p,modDist);



    if (scale < 3.){
        scale = 1. + rOrig.y*3.;
    	p -= r*6.*scaleO;
    } else {
    	p -= r*6.*scaleO;
    }
    //p -= clamp(r*4., vec3(-1.4), vec3(1.4))*6.;

    p.xy *= rot(r.x*19. + iTime*r.z*1.);
    p.yz *= rot(r.y*19. + iTime*r.x*1.);
    //p.xz *= rot(r.z*19. + iTime*r.y*1.);

    //p.xz *= rot(r.y*29.);
    p = abs(p);


    for (int i = 0; i < 2 ; i++){
    	p = abs(p);
        //p.xy *= rot(0.4);
        //p.yz *= rot(0.4);
        p.xz *= rot(r.y*0.7 * step(0.,r.x));
        p.z -= .2;
        p.xy *= rot(sign(r.x)*PI*0.25);
        p.yz *= rot(r.y*2.*0.2);
        //p.y -= 0.2 * r.x*scale*2.;
    }


    p -= 1.*scale;
    float d = max(p.x, max(p.y, p.z));

    d = max(d, dot(p + 0.33*scale, normalize(vec3(1))));


    p = max(p,-(abs(p.y*0.5) - 0.7));

    d *= 0.2;
    return d;

}
vec3 glow = vec3(0);

#define dmin(a, b) a.x < b.x ? a : b
vec2 map(vec3 p){
	vec2 d = vec2(10e7);
    p.xy *= rot(0.3);

    d =  dmin(d,vec2(sdBlocksA(p, 1.), 0.));
    p.xz *= rot(0.4);
    d =  dmin(d,vec2(sdBlocksA(p, 10.), 0.));
    p.yz *= rot(0.4);
    d =  dmin(d,vec2(sdBlocksA(p, 4.), 0.));

    return d;
}

vec3 getNormal(vec3 p){
	vec2 t= vec2(0.1,0.);
    return normalize( map(p).x -
    	vec3(
        	map(p - t.xyy).x,
        	map(p - t.yxy).x,
        	map(p - t.yyx).x
        )
    );

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);


    vec3 ro = vec3(0,0,0. + mx + iTime*90.);
    ro.y += sin(iTime)*50.;
    ro.x += sin(iTime + sin(iTime*0.4)*1.5)*100.;
    vec3 roO = ro;
    vec3 lookAt = ro + vec3(0,0,1);
    lookAt.x += sin(iTime);
    vec3 rd = getRd(ro, lookAt, uv);

    //rd.xz *= rot(sin(iTime*0.1));
    vec3 rdO = rd;
    vec3 p = ro; float t = 0., tO = 0.;
    vec3 att = vec3(1);

    const int iterations = 200;
    float bounce = 0.;
    for (int i  = 0;i <= iterations + min(0, iFrame); i++){
    	vec2 d = map(p);
        glow += vec3( exp(-d.x) );
        if(d.x < 0.001){
            vec3 lightPos = roO + vec3(0,1,0);
            //lightPos = vec3(0,0,0);
            vec3 lightCol = vec3(1.);

            vec3 L = normalize(lightPos - p);
            //L = normalize(vec3(-1));
            vec3 H = normalize(L - rdO);
            //H *= 1.;
            //vec3 lightCol = vec3(0.5,0.5,0.2);


            int id = int(d.y);
            float METALNESS = mats[id].metalness;
            float ROUGHNESS = mats[id].roughness;
            vec3 albedo = mats[id].albedo;
            albedo = vec3(1);
            vec3 N = getNormal(p);
            vec3 V = normalize(ro - p);
            //vec3 V = -rd;

            vec3 F0 = vec3(0.04); 
            F0 = mix(F0, albedo, METALNESS);


            // calculate per-light radiance
            float distL    = length(lightPos - p)*0.003;
            float attenuation = 1.0 / (distL * distL);
            attenuation = clamp(attenuation, 0. ,10.);
            //float attenuation = dist*0.02;
            //attenuation = 1.;
            vec3 radiance     = lightCol * attenuation;        

            // cook-torrance brdf
            float NDF = DistributionGGX(N, H, ROUGHNESS);   
            float G   = GeometrySmith(N, V, L, ROUGHNESS);      
            vec3 F    = fresnelSchlick(clamp(dot(N, V), 0.0, 1.0), F0);     

            vec3 kS = F;
            vec3 kD = vec3(1.0) - kS;
            kD *= 1.0 - METALNESS;	  

            vec3 numerator    = NDF * G * F;
            float denominator = 4.0 * max(dot(N, V), 0.0) * max(dot(N, L), 0.0);
            vec3 specular     = numerator / max(denominator, 0.001);  

            // add to outgoing radiance Lo
            float NdotL = max(dot(N, L), 0.0); 
            att *= vec3(.62,.576,.388)*1.4;
            col += (kD * albedo / PI + specular) * radiance * NdotL * attenuation*att; 

            vec3 ambient = vec3(0.01) ;          

            if (bounce == 0.){
            	tO = t;
            }
            bounce++;
            ro = p;
            rd = reflect(rd, N);
            t = 0.;
            d.x = 2.6;
            if (bounce > 2.){
                break;
            }

        }

        if(t > 300. || i == iterations){
            if (bounce == 0.){
            	tO = t;
            }
            break;
        }   
        t += d.x;
    	p = ro + rd*t;
    }
    col += glow*0.0007*vec3(.62,.576,.388)*exp(-tO*0.005);
    col *= 1.5;
    //col = mix(col, vec3(0.1), smoothstep(0.,1.,tO*0.002));
    col = pow(col, vec3(0.45));
    col = smoothstep(0., 0.8,col);

    // Output to screen
    fragColor = vec4(col,1.0);
}

