//    
// Testing Sebastian Aaltonen's soft shadow improvement
//
// The technique is based on estimating a better closest point in ray
// at each step by triangulating from the previous march step.
//
// More info about the technique at slide 39 of this presentation:
// https://www.dropbox.com/s/s9tzmyj0wqkymmz/Claybook_Simulation_Raytracing_GDC18.pptx?dl=0
//
// Traditional technique: https://iquilezles.org/articles/rmshadows
//
// Go to lines 54 to compare both.


// make this 1 is your machine is too slow
#define AA 2

#define tMin 0.001
#define tMax 20.1
//------------------------------------------------------------------
const mat2 m = mat2( 0.80,  0.60, -0.60,  0.80 );

float hash( float n ){
    return fract(sin(n)*43758.5453);
}
float noise( in vec2 x ){
    vec2 p = floor(x);
    vec2 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0;

    return mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
               mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
}
float fbm( vec2 p ){
    float f = 0.0;

    f += 0.50000*noise( p ); p = m*p*2.02;
    f += 0.25000*noise( p ); p = m*p*2.03;
    f += 0.12500*noise( p ); p = m*p*2.01;
    f += 0.06250*noise( p ); p = m*p*2.04;
    f += 0.03125*noise( p );

    return f/0.984375;
}

float sdPlane( vec3 p )
{
	return p.y;
}
float sdSphere( vec3 p ,float r)
{
	return length(p)-r;
}
float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
vec2 min2(vec2 a, vec2 b){
    return (a.x < b.x) ? a : b;
}

//------------------------------------------------------------------

vec2 map(vec3 pos){
    vec3 qos = 1.*vec3( (fract(pos.x+.5)-.5)*1., pos.y,fract(pos.z+0.5)-0.5 );//repeatSpace on xz axis
    return min2(vec2(sdPlane(pos-vec3(0)),0.)  ,  vec2(sdBox(pos-vec3(0.,.2,0.),vec3(.2)) ,1.));
}
//------------------------------------------------------------------

float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    float ph = 1e10; // big, such that y = 0 on the first iteration

    for( int i=0; i<32; i++ )
    {
		float h = map( ro + rd*t ).x;

        // traditional technique

        // improved technique

        // use this if you are getting artifact on the first iteration, or unroll the
        // first iteration out of the loop
        //float y = (i==0) ? 0.0 : h*h/(2.0*ph); 
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, 10.0*d/max(0.0,t-y) );
        ph = h;


        t += h;

        if( res<0.0001 || t>tmax ) break;

    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
	float mat=-1.;


    float t = tMin;
    for( int i=0; i<64; i++ )
    {
	    float precis = 0.0005*t;
        vec2 a=map( ro+rd*t);
	    float res =a.x;
        if( res<precis || t>tMax ){
            mat=a.y;
        	break;
    	}
        t += res;
    }

    if( t>tMax ) t=-1.0;
    return vec2(t,mat);
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.001 + 0.15*float(i)/4.0;
        float d = map( pos + h*nor ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 1.5*occ, 0.0, 1.0 );    
}
vec3 shader2(vec2 uv){
	//WE NEED TO GO DEEPER
    uv*=1.5;
    float r= length(uv);
    float a=atan(uv.x,uv.y)+iTime;
    float f=cos(a*4.)/2.;
    return vec3(clamp(step(f,r),.1,1.)/3.)*texture(iChannel0,uv).xyz/1.3;
}
vec3 reflectRay(vec3 rd, vec3 n){

	return rd-2.*dot(rd,n)*n;
}
vec3 render( in vec3 ro, in vec3 rd, int depth)
{ 
    vec4 materials[3];
    //rgb reflection 1 is [perfect mirror. 0 is totally matte
    materials[0]=vec4(.4,.3,.2,0);//background
    materials[1]=vec4(.7,.0,0,0.5); 
    materials[2]=vec4(.7,.0,1,0); 

    vec3  col = vec3(0);
    vec2 a = castRay(ro,rd);
	float t=a.x;
    for (int i =0; i<= depth; i++){
    if( t>-.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );

        // material       
        vec2 matUV=mix(mix(vec2(pos.xz),vec2(pos.zy), nor.x), pos.xy ,nor.z);//YAY
		materials[0]=vec4(shader2(fract(matUV-.5)-.5),0.);        
        //materials[0]=reflect(rd,nor);
		//materials[1]=reflect(rd,nor);

		vec4 mate = materials[int(a.y)];
		    // key light
    	    vec3  lig = normalize( vec3(-0.1, 0.3, 0.6) );
	        vec3  hal = normalize( lig-rd );
    	    float dif = 1.5*clamp( dot( nor, lig ), 0.0, 1.0 ) * 
                    calcSoftshadow( pos, lig, 0.01, 3.0 );

			float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),16.0)*
                    dif *
                    (0.04 + 0.96*pow( clamp(1.0+dot(hal,rd),0.0,1.0), 5.0 ));

    	    float spec=2.;
        	vec3 lig_col = vec3(1.0,1.0,1.);
			col = mate.xyz *2.0*dif*lig_col;
    	    col +=      spec*spe*lig_col;

	        // ambient light
    	    float occ = calcAO( pos, nor );
			float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
    	    col += mate.xyz*amb*occ;        
	        // fog
    	    col *= exp( -0.0005*t*t*t );

    }
    }

	return col;
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
