// === move particles ====================================

void mainImage( out vec4 O, vec2 U )
{
    O = T(U);

    if (iFrame==0 || O.w<0.) {  // --- called at init + particle timer
        vec3 r = hash3(vec3(U,iTime));
        if (iFrame==0) O.xy = r.xy *R/R.y;  // xy = normalized location
        float d = floor(r.z*4.);
        O.z = iFrame==0 ? d                 // z = direction: random
                        : mod( O.z + sign(d-1.5), 4.); // or random turn
        O.w = fract(r.z*4.)*60.;            // w = life time of current move
        return;
    }
    if (U.y>1.) return;                     // limit particles

                                     // --- move particle
    float d = sign(O.z-1.5) * radius/2.;    // dir = + or - ?
    int(O.z)%2==0                           // orientation = x or y ?
        ? O.x = fract((O.x+d)/ratio)*ratio  // screen wrap
        : O.y = fract((O.y+d));
    O.w--;                                  // timelife decrease 
}

