}
float nois(float t){
    float n = 0.;
    n = fract(rnd(t)*rnd(t*2.41));
    vec2 p = vec2(fract(rnd(t*14.11)),fract(rnd(t*124.1)*rnd(t*3.15)));
    vec2 q = vec2(fract(rnd(t*14.124)*rnd(t*41.15)), n*0.15);
