vec2 snare(float t) {
	vec2 s = vec2(0);
	t = mod(t + hbeat, beat);

    float env = exp(-t*20.);
    float noise = noise(vec2(t))*env;
    //s.x += ;
    s.x += sin((180. + env*150. + noise*20.)*tau*t )*env;
    s.y = s.x;

    return s;
