vec2 kickSecond(float t, float phase) {
    vec2 s = vec2(0);
    float r = notVeryRandom( mod(floor(t/ hbeat), 26.));
    float rB = notVeryRandom( mod(floor((t)/ hbeat) + 4., 26.));

    float limit = 0.26;
    if (P(4.)){
  		limit = 0.02;   
    }

    if (rB < limit) {
        r *= 3.99;
		r = floor(r);
        r += 1.;
        float offs = hbeat * 1./r;
        if (offs == hbeat) {
        	offs -= hbeat/4.;
        }
        t = mod(t + offs, hbeat);

        float env = exp(-t*10.);
        if (phase == 1.) {
        	s.x += sin( (140. * env+ 10. ) *tau*t) * env ;
        } else if (phase == 2.) {
        	s.x += sin( (240. * env+ 10. ) *tau*t) * env ;
        }

    	//s.x += sin( (random(t)) *tau*t) * env*2. ;

        s.x = clamp(s.x*1.6,-1., 1.);


        s.y = s.x;

