	return s;
}
vec2 lead(float t) {
	vec2 s = vec2(0);

    float note;
    if (P(4.)){
    	//note = arpB(t, beat, 1., 4., 14.2);
        note = arpB(t, beat, 1., 8., 39.9);
    	t = mod(t,beat);
    } else if (P(5. + 3.)){
    	note = arpB(t, beat, 1., 8.,  20.9);
    	t = mod(t,beat);
    } else if (P(8.)){
    	note = arpB(t, beat, 1., 8.,  20.9);
    }
    float env = exp(-t*9.);

    float iterations = 5.;
    float freq = note;
    float scale = 1.;
    for (float i = 0.; i < iterations; i++) {
        float r = random(i)*10.;
    	s.x += sin((note + r)*tau*t )*env*scale;
    	s.x += sin(note*tau*(t + 0.05) )*env*scale;
    	s.x += sin(0.5*note*tau*(t + 0.06) )*env*scale;
        scale *= 0.8;
        s.x * 0.33;
        note *= 2.01;
    }
    s.x /= iterations;
    s.y = s.x;
    return s;
}
vec2 echoChannel(float t) {
	vec2 s = vec2(0);

    float fb = 0.85, tm = qbeat*1.5, cf = 0.9, ct = tm;
    // tap 1 
    s += lead(t) * cf; cf *= fb; 

    s += lead(t) * cf; cf *= fb; 
    // tap 2
    s += lead(t - ct) * cf; cf *= fb; ct += tm;
    // tap 3
    s += lead(t - ct) * cf; cf *= fb; ct += tm;
    // tap 4
    s += lead(t - ct) * cf; cf *= fb; ct += tm;
    // tap 5
    s += lead(t - ct) * cf; cf *= fb; ct += tm;

    return s;
}



