float genSaw(float time, float freq, bool useJitter)
{
    float jitter1 = 0.0;
    float jitter2 = 0.0;

    if(useJitter)
    {
      jitter1 = texture(iChannel0, vec2(time,0.2)*0.01).x;
      jitter2 = texture(iChannel0, vec2(time-0.01,0.2)*0.01).x;
    }

    float val = fract(time*(freq + (jitter1+jitter2)*0.2));
    return val;
}

float genNiceSaw(float time, float freq, bool useJitter)
{
    return (genSaw(time,freq,useJitter) - 0.5) * 2.0;
}
#define maxTaps 40
/* Reso lowpass saw... truncated IIR #yoloswag */
float resoLpSaw(float time, float freq, float fa)
{

    fa= sqrt(fa);
	float reso = 0.1;   
    float c = pow(0.5, (128.0-fa*128.0)   / 16.0);
  	float r = pow(0.5, (reso*128.0+24.0) / 16.0);

    float v0,v1;
    v0 = 0.0;
    v1 = 0.0;

    for(int i = 0; i < maxTaps; i++)
    {
          float history = float(maxTaps-i)*(1.0/44100.0);

          float t = time - history;
          float inp = genNiceSaw(t,freq,false);
          v0 =  (1.0-r*c)*v0  -  (c)*v1  + (c)*inp;
  		  v1 =  (1.0-r*c)*v1  +  (c)*v0;
    }

    return v1;
}
