// https://github.com/hughsk/glsl-hsv2rgb
// Used for changing the light colour
vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


float luminance(vec3 col) {
    return dot(col, vec3(0.299, 0.587, 0.114));
}

// Use pixel luminance for height
float texelHeight(vec4 rgba) {
    return luminance(rgba.rgb);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normal map strength
    const float normal_strength = 7.0;

    // Bump height multiplier
    const float height_multiplier = 4.0;
    const float texel_offset = 0.5;

    vec2 uv = fragCoord/iResolution.xy;

    vec3 offset = vec3(texel_offset / iChannelResolution[0].xy, 0.0);

    vec3 surfaceColor = texture(iChannel0, uv).rgb;
	float height = texelHeight(surfaceColor.rgbb) * height_multiplier;
    vec3 worldPosition = vec3(uv, height);

    // Take a difference between the right texel and the left texel
    float ddX = texelHeight(texture(iChannel0, uv + offset.xz))
        	  - texelHeight(texture(iChannel0, uv - offset.xz));

    // Take a difference between the bottom texel and the top texel
    float ddY = texelHeight(texture(iChannel0, uv + offset.zy))
        	  - texelHeight(texture(iChannel0, uv - offset.zy));

    ddX *= normal_strength / texel_offset;
    ddY *= normal_strength / texel_offset;

    // I'm probably using the wrong nomenclature here don't sue me
    vec3 tangent   = normalize(vec3(1.0, 0.0, ddX));
    vec3 bitangent = normalize(vec3(0.0, 1.0, ddY));

    // As easy as one two seven NaN NaN Segmentation fault
    vec3 normal = cross(tangent, bitangent);

    vec2 lightPosition = 0.5 + 0.4 * vec2(cos(iTime), sin(iTime));
    vec3 lightColor = hsv2rgb(vec3(mod(iTime * 0.1, 1.0), 0.1, 1.0));
    float lightStrength = 10.0;
    float lightHeight = 2.8;

    vec3 lightDir = normalize(vec3(lightPosition, lightHeight) - worldPosition);

    float attenuation = lightStrength / pow(distance(lightPosition, uv) + lightHeight, 2.0);

    float lambert = max(0.0, dot(lightDir, normal));
    float blinnPhong = pow(max(dot(normalize(vec3(vec3(0.0, 0.0, 1.0)) + normal), lightDir), 0.0), 10.0);

    float ambient = 0.0;
    float diffuse = 0.5 * lambert;
    float specular = 0.5 * blinnPhong;

    vec3 col = ambient + (diffuse * surfaceColor + specular) * attenuation * lightColor;

    float luma = luminance(col);
    luma *= 3.5;
    col /= luma / (luma + 1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define AA 1

float noise(vec3 p)
{
	vec3 ip=floor(p);
	p-=ip;
	vec3 s=vec3(7, 157, 113);
	vec4 h=vec4(0, s.yz, s.y+s.z)+dot(ip, s);
	p=p*p*(3.-2.*p);
	h=mix(fract(43758.5*sin(h)), fract(43758.5*sin(h+s.x)), p.x);
	h.xy=mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

float fbm5( vec3 p )
{
	//p += vec3(1.0,1.0,0.8);
    float f;
    f  = 0.5000*noise( p); p = p*2.04;
    f += 0.2500*noise( p); p = p*2.03;
    f += 0.1250*noise( p ); p = p*2.01;
    //f += 0.0625*noise( p ); p = p*2.02;
    //f += 0.03125*noise( p ); 

	float n = noise( p*3.5 );
    f += 0.03*n*n;

    return f;
}


vec2 map(in vec3 p)
{   
    // monolite
    vec3 pm = p; pm.z-=75.0; pm.y+=0.2;
    pm.z+=iTime;

    pm.yz *= rot(-2.4)*noise(pm*0.15);
    //pm.xz+=noise(pm);
    pm.x = mod(pm.x, 5.0)-2.5;
    pm.z = mod(pm.z, 10.0)-5.0;

    vec2 res = vec2(sdCube((pm), vec3(0.25, 5.0+noise(pm*0.1), 0.15)*2.0), 0.1);

    // monolite fractures
    // inspired by iq's https://www.shadertoy.com/view/3lsSzf
    float yy = p.y-0.62+0.45+noise(p*14.0)*1.0*p.x*p.x;
    res.x += 0.01*(1.0-smoothstep(0.0,0.02,abs(yy)));
    yy = p.y+0.65-0.95-noise(p*7.0)*3.0*p.x*p.x;
    res.x += 0.01*(1.0-smoothstep(0.0,0.0015,abs(yy)));
    yy = p.y-0.15+1.45-noise(p*7.0)*3.0*p.x*p.x;
    res.x += 0.01*(1.0-smoothstep(0.0,0.0015,abs(yy)));

    // ground
    float n = fbm5(p);
    float h = mix(clamp(n, 0.0, 0.5), sin(p.x*0.2), n);
    h+=clamp(n, 0.0, 0.5);
    float m = p.y-1.5+h*1.51;
    res = smin(res, vec2(m, 2.0), 2.0);
    return res;
}

vec2 raymarch(vec3 ro, vec3 rd)
{
    float tmin = 0.0001;
    float tmax = 120.0;
	float t = tmin;
    vec2 res = vec2(0, -1);

    for (int i = 0; i < 100 && t < tmax; i++)
    {
    	vec2 d = map(ro + rd * t);
        if (d.x < 0.0001*t){
        	res = vec2(t, d.y);
            break;
        }

        t+=d.x;
    }
    if(t>120.0) res = vec2(-1);
    return res;
}

vec4 mapClouds( in vec3 p )
{
	float d = 1.0-0.3*abs(0.5 - p.y);
    p.x+=iTime*2.0;
	d -= 1.1*fbm5( p*0.45 );
	d = clamp( d, 0.0, 1.0 );

	vec4 res = vec4( d );

	res.xyz = mix( 0.8*vec3(1.0,0.95,0.8), 0.2*vec3(0.6,0.6,0.6), res.x );
	res.xyz *= 0.65;

	return res;
}


vec3 lig = vec3(-1.1, 0.5, -1.5);
vec4 raymarchClouds( in vec3 ro, in vec3 rd, in vec3 bcol, float tmax, out float rays, ivec2 px )
{
	vec4 sum = vec4(0, 0, 0, 0);
	rays = 0.0;

	//float sun = clamp( dot(rd,lig), 0.0, 1.0 );
	float t = 0.1;//*texelFetch( iChannel0, px&ivec2(255), 0 ).x;
	for(int i=0; i<80; i++)
	{
		if( sum.w>0.99 || t>tmax ) break;
		vec3 pos = ro + t*rd;
		vec4 col = mapClouds( pos );

		float dt = max(0.1,0.05*t);
		float h = pos.y;
		float c = noise( (pos + lig*h)*0.35 );
		rays += 0.02*(smoothstep( 0.38, 0.6, c ))*(1.0-col.a)*(1.0-smoothstep(2.75,2.8,pos.y));

		col.xyz *= vec3(0.4,0.52,0.6);
        //col.xyz += vec3(1.0,0.7,0.4)*0.4*pow( sun, 6.0 )*(1.0-col.w);
		col.xyz = mix( col.xyz, bcol, 1.0-exp(-0.0018*t*t) );

		col.a *= 0.5;
		col.rgb *= col.a;

		sum = sum + col*(1.0 - sum.a);	

		t += dt;//max(0.1,0.05*t);
	}
    rays = clamp( rays, 0.0, 1.0 );

	return clamp( sum, 0.0, 1.0 );
}

const float maxHei = 0.8;

// https://iquilezles.org/articles/rmshadows
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    // bounding volume
    float tp = (maxHei-ro.y)/rd.y; if( tp>0.0 ) tmax = min( tmax, tp );

    float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        float s = clamp(8.0*h/t,0.0,1.0);
        res = min( res, s*s*(3.0-2.0*s) );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );

}

vec3 calcCloudsNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.0005;
    return normalize( e.xyy*mapClouds( pos + e.xyy ).x + 
					  e.yyx*mapClouds( pos + e.yyx ).x + 
					  e.yxy*mapClouds( pos + e.yxy ).x + 
					  e.xxx*mapClouds( pos + e.xxx ).x );

}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}

// lighting from iq - https://www.shadertoy.com/view/Xds3zN
vec3 render(vec3 ro, vec3 rd, vec2 fragCoord)
{
    vec2 t = raymarch(ro, rd);
    vec3 p = ro+rd*t.x;
    vec3 col = vec3(0.0);
    vec3 nor = calcNormal(p);
	vec3 cnor = calcNormal(p);

    if (t.y < 0.0) {
    	col = vec3(0.01);
        return col;
    }

    // monolite
    if (t.y == 0.1) {
        //col = vec3(.001);
        //return col;
    }

    float tx = t.x*nor.z;
    col = nor*clamp(tx, -.5, 1.5);
    col = 0.7 + 0.5*col;

    // sun reflections
    col += vec3(1.0,0.5,0.2)*0.35*pow( nor.x, 3.0 );

    // fog
  	col = mix( col, vec3(0.0), 1.0-exp( -0.0001*t.x*t.x*t.x) );
    //col = mix(col, vec3(0.5, 1.1, 0.7), t.x*0.00015);

    // clouds	
    float rays = 0.0;
    vec3 bcol = vec3(1.0);

    {
	if( t.x<0.0 ) t.x=100.0;
    vec4 res = raymarchClouds( ro, rd, bcol, t.x, rays, ivec2(fragCoord) );
	col = mix(col, 0.5-col*(1.0-res.w) + res.xyz * (vec3(0.9)), res.w*0.81);
	}

	//col = clamp( col, 0.0, 1.0 );

	//col = sqrt(col);
    return col;
}

vec3 getCameraRayDir(vec2 uv, vec3 camPos, vec3 camTarget)
{	
    // Calculate camera's "orthonormal basis", i.e. its transform matrix components
    vec3 camForward = normalize(camTarget - camPos);
    vec3 camRight = normalize(cross(vec3(0.0, 1.0, 0.0), camForward));
    vec3 camUp = normalize(cross(camForward, camRight));

    float fPersp = -1.5;
    vec3 vDir = normalize(uv.x * camRight + uv.y * camUp + camForward * fPersp);

    return vDir;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * (fragCoord/iResolution.xy - 0.5);
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0.0, 5.0, 10.0+iTime*0.25);
    vec3 ta = vec3(-9.0, 10.0, 0);

    ro.x = sin(iTime*0.01);
    //ta.x = cos(iTime*0.1);

    vec3 tot = vec3(0.0);

    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;

    	vec3 rd = getCameraRayDir(p, ro, ta);   
    	vec3 col = render(ro, rd, fragCoord);
        tot+=col;
        col = pow( col, vec3(0.4545) );
    }

    tot /= float(AA*AA);
    fragColor = vec4(tot,1.0);
}

