    float id = floor(p.z/sep);
    p.z = mod(p.z, sep) -sep/2.;

    float reps = 5.;
	pModPolar(p.xy, reps);    
    vec3 polarP = vec3(reps - 1., 0., 0.);

    for (float i = 0.; i < 3.; i++){
    	p.y -= 0.5;
    	p.x -= 1.5 - curve(T + id, 1.5)*1.9;
    	p=abs(p);
		p.z -= 0.9 + fract(id*2144.124)*0.5;

    	p.yz *= rot(4.5 + sin(T*0.2  + id)*0.5);
    	p.xz *= rot(0.4);
    }

    p -= polarP;
    d = min(d, sdGeometry(p));

    return d;
}
float map(vec3 p){
	float d = 100.;
    float dStruct;
    float dTunnGround;
    if (scene == 1){
    	dStruct = sdStructScene1(p);
        dTunnGround = 10000.;
    }
    else if (scene == 2){
    	dStruct = sdStructScene2(p);
        dTunnGround = sdGround(p);
    }
    d = min(dStruct, d);
    d = min(d, dTunnGround);
   	dStruct <= d ? id = 1 : dTunnGround <= d ? id = 2 : id = 0;
    return d;
}

vec3 normal (vec3 p){
	vec2 e = vec2(0.001, 0);
	return normalize(map(p) - vec3(
    	map(p - e.xyy),
    	map(p - e.yxy),
    	map(p - e.yyx)
    ));

}

vec3 shake(vec3 p){
	p.y += fract(sin(p.z*1.1))*0.01;
	p.x += fract(sin(p.z*2.1))*0.01;
    return p;
}
#define spectra(t) (0.9  + sin(t + 1.5 + 2.*vec3(0.1,0.4,0.6) )*0.4   )
#define spectra2(t, beat) (-0.5 + beat  + sin(t + 4.9 + 1.*vec3(0.9,0.4,0.6) )*0.6   )
vec4 render(vec2 uv){
	vec3 col = vec3(0);

    vec3 ro;
    vec3 lookAt;
    float trig;
    if (scene == 1){
        float z = 30.;
        ro = vec3(0. , 0,-1. );
        ro.x += sin(mx)*z; ro.z += cos(mx)*z;
        lookAt = vec3(0. ,0,0.);
        trig = 1.;
    } else if (scene == 2){
        ro = vec3(0. , 0,-11. + my);
        #ifdef shaking
    	ro = shake(ro);
        #endif
        ro.xy -= offsTunn(ro);
        lookAt = vec3(0. ,0,-8. + my);
        lookAt.xy -= offsTunn(ro + vec3(0,0,3));
        trig = exp(-mod(T, 0.5));
    }

    vec3 rd = getRd(ro, lookAt, uv);

    vec3 glow = vec3(0);
    vec3 glow2= vec3(0);
    float t = 0.; vec3 p = ro;
    for (int i = 0; i<max_steps; i++){
    	float h = map(p);

        if (h < min_dist || t > max_dist) break;
        glow += spectra(t*0.05)*0.02;
        glow2+= spectra2(t*0.05, trig)*0.02;
        t += h;
        p += h * rd;
    }
    if (scene == 1){
        glow2.g *= 0.9;
        col =  glow2*20.; 
        col *= 0.1;

        col -= t*0.0009;
        //col *= shade(p, ro, rd);
    }
    else if (scene == 2){
        if (id == 2){
            //col -= DGROUND*0.01;
            col = glow ;
            float restraint = clamp(exp(-length(glow)*1.95 + 2.34),0., 1.);
            //col *= restraint;
            //col.b *= restraint;
            //col.b = col.b*0.4 + col.b*restraint*0.6;

            col.g = pow(col.g, 2.);
            //col.g -= DGROUND*0.001;
        } else {
            glow2.g *= 0.9;
            col =  glow2*20.; 
            col *= 0.1;
            //col *= shade(p, ro, rd);
        }

    }


    //col += t*0.02;


    return vec4(col, t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec4 col = render(uv);

    fragColor = vec4(col);
}

