        } else if (t<msr){
        	s += sAmbG(t, note(3.,2.));
    		s += sAmbG(t, note(7.,2.));
    		s += sAmbG(t, note(9.,2.));
            if (t>msr -hmsr/2.) s += sAmbG(t, note(10.,2.));
        } else if (t<msr+hmsr){
        	s += sAmbG(t, note(4.,2.));
    		s += sAmbG(t, note(7.,2.));
    		s += sAmbG(t, note(9.,2.));
        }
		else if (t<msr*2.){
        	s += sAmbG(t, note(3.,2.));
    		s += sAmbG(t, note(7.,2.));
    		s += sAmbG(t, note(9.,2.));
            if (t>msr -hmsr/2.) s += sAmbG(t, note(10.,2.));
        }    	

    } else if (M(2.) || M(3.)){
        t = mod(t, msr*2.);
        if (t<hmsr){
        	s += sAmbG(t, note(5.,2.));
    		s += sAmbG(t, note(10.,2.));
    		s += sAmbG(t, note(13.,2.));
        } else if (t<msr){
        	s += sAmbG(t, note(7.,2.));
    		s += sAmbG(t, note(11.,2.));
    		s += sAmbG(t, note(13.,2.));
            if (t>msr -hmsr/2.) s += sAmbG(t, note(10.,2.));
        } else if (t<msr+hmsr){
        	s += sAmbG(t, note(8.,2.));
    		s += sAmbG(t, note(11.,2.));
    		s += sAmbG(t, note(13.,2.));
        }
		else if (t<msr*2.){
            float expMod = exp(-mod(t - msr -hmsr/2., msr)*0.2)*0.5;
            float revExpMod = exp(mod(msr+hmsr, msr)*0.05 - 1.)*0.5;
            float pitchDown =  1. + revExpMod*sin(t*(30. + revExpMod*400.))*0.04 - expMod;
        	s += sAmbG(t, note(7.,2.)*pitchDown);
    		s += sAmbG(t, note(11.,2.)*pitchDown);
    		s += sAmbG(t, note(13.,2.)*pitchDown);
            if (t>msr -hmsr/2.) s += sAmbG(t, note(10.,2.)*pitchDown);
        }    	

    }
    return s;
}
vec2 gIntroAmb(float t){
    vec2 s = vec2(0);

    float iters = 30.;
    if (M(0.) || M(1.)){
        for (float i = 0.; i<iters; i++){
            float f = 1900.+ hash(i)*(170. + sin(t)*300. );
            f*= 0.33;
            float tex = texture(iChannel0, vec2(i/1000.)).x;
            s += (sin(t*f)*noise(vec2(f*i*0.1,f*i*0.11))*tex)/iters;
        }
    } else if(M(2.) || M(3.)){
        for (float i = 0.; i<iters; i++){
            float m1 = exp(t*0.25 - 4.)*13.;
            float m2 = sin(t*(1. + m1*0.006));
            float f = 1900.+ hash(i)*(170. + m1 + m2  );
            f*= 0.33;

            if (t>(msr*3.75)){
                float h = t - msr*3.75;
                f *= 1. + (1./(h+1.)+ 1.)*4.; 
            }
            float tex = texture(iChannel0, vec2(i/1000.)).x;
            s += (sin(t*f)*noise(vec2(f*i*0.1,f*i*0.11))*tex)/iters;
        }
    } 
    s *= 4.;
    return s;

}

