    vec2 s = vec2(0);

    //t*=2.;
    //t += start_offset;

    float volHats = 0.6;

    if (M(5.) || M(6.) || M(7.) || M(8.) ){
    	volHats = 0.94;
    }



    s += gKick(t)*0.5;
    s += gSnare(t)*1.;
    s += gHats(t)*volHats;
    s += gHats2(t)*0.2;
    s += gBass(t)*0.115;
    s += gSub(t)*0.4;
    s += gPadSaw(t)*0.2;
	s += gIntroAmb(t)*0.5;

    if (t < msr*4.){ s*= 0.45;};
    if (M(3.)){
    	if (t > msr*3.95)
        {
        	s *= exp(-(t - msr*3.95)*0.5);
        }
    }

    s *= 0.6;
    s = clamp(s, -1., 1.);
    return s;
}

