vec3 getRd(vec3 ro, vec3 lookAt, vec2 uv){
	vec3 dir = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0,1,0), dir));
    vec3 up = normalize(cross(dir, right));
    return dir + right*uv.x + up*uv.y;
}
#define pal(x,t) (0.5 + 0.5*sin(vec3(1.4,1.1,1.9)*t + x))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(0. + sin(iTime*0.5)*0.7,0. + sin(iTime*0.8)*0.2,-4);
    //vec3 ro = vec3(0. + 1.5,0. + sin(iTime*0.8)*0.2,-4);

	ro.z += iTime*10. + mx;
    vec3 lookAt = ro + vec3(0,0,5);;
    lookAt +=  vec3(0. + sin(iTime*0.4),0. + sin(iTime*0.4)*0.5,1);
    vec3 rd = getRd(ro, lookAt, uv);

    float bounce = 0.;
    vec3 attenuation = vec3(1.);
    float side = 1.;
    float t = 0.; vec3 p = ro;
    float tO = 0.;
    vec3 pO = p;
    for (int i = 0; i < 300; i++){
    	vec2 d = map(p);
        d.x *= side;

        if (d.x < 0.001){
            vec3 n = getNormal(p)*side;

            vec3 lD = normalize(vec3(-1,-2,1));
            vec3 h = normalize(lD - rd);

            float diff = max(dot(n,lD), 0.); 
            float fres = pow(1. - max(dot(-rd, n), 0.), 8.);
            float spec = pow(max(dot(n, h), 0.), 2.);
            vec3 albedo = vec3(0.2,0.5,0.9)*0.1;
            col += mix(albedo*diff, vec3(pal(fres*4. + 5.,0.9 + fres)*attenuation*(spec*fres)*9.54), 0.7)*attenuation;

