            //col += pow(1. -fres*spec*200., 5.)*pal(fres*9.,9.5)*attenuation*0.1;



            if (bounce == 0.){
                tO = t;
                pO = p;
            }
            attenuation *= vec3(0.6,0.7,0.9)*0.6;
            //side = -side;
            bounce++;
            //rd = refract(rd,n, 1. + n.x*0.4 + n.y*0.3);
            rd = reflect(rd,n );
            ro = p;
            t = 0.;
            d.x = 0.1;
        	//break;
        }
        if (t > 100.){
            if (bounce == 0.){
            	tO = t;
                pO = p;
            }
        	break;
        }

        t += d.x;
		p = ro + rd*t;
    }

    float T = iTime*4.;
    if(bounce > 0.){
        float tInterval = 4.;
        float tId = floor(iTime/tInterval);
        float tM = mod(iTime,tInterval);
        float rC = r11(tId);
        float rN = r11(tId + 1.);

        vec3 modeA = pow(0.6 + sin(
            sin(pO.z*5.4 + T)
        )*0.5, 50.)*vec3(1.)*pal(5.5 ,9.8 + sin(iTime)*0.1);
        vec3 modeB = pow(0.6 + sin(
            sin(pO.x*0.2 + T + pO.y*0.4*sin(iTime )*pO.x*2. + sin(iTime))
        )*0.5, 100.)*vec3(1.)*pal(2.5 ,6.8 + sin(iTime)*0.1);
        vec3 modeC = pow(0.6 + sin(
            sin(pO.y*0.01 + T*1. + pO.z*0.2)
        )*0.48, 100.)*vec3(1.)*pal(5.5 ,6.8 + sin(iTime)*0.1);
        vec3 currMode, nextMode = vec3(0);
        float currId = floor(rC*2.99), nextId = floor(rN*2.99);

        currId == 0. ? currMode = modeA : currId == 1. ? currMode = modeB : currId == 2. ? currMode = modeC : currMode;
        nextId == 0. ? nextMode = modeA : nextId == 1. ? nextMode = modeB : nextId == 2. ? nextMode = modeC :  nextMode;

        col += mix(currMode,nextMode, pow(tM/tInterval, 3.));

