vec3 RGBToYCoCg( vec3 RGB )
{
	float Y = dot(RGB, vec3(  1, 2,  1 )) * 0.25;
	float Co= dot(RGB, vec3(  2, 0, -2 )) * 0.25 + ( 0.5 * 256.0/255.0 );
	float Cg= dot(RGB, vec3( -1, 2, -1 )) * 0.25 + ( 0.5 * 256.0/255.0 );
	return vec3(Y, Co, Cg);
}

vec3 YCoCgToRGB( vec3 YCoCg )
{
	float Y= YCoCg.x;
	float Co= YCoCg.y - ( 0.5 * 256.0 / 255.0 );
	float Cg= YCoCg.z - ( 0.5 * 256.0 / 255.0 );
	float R= Y + Co-Cg;
	float G= Y + Cg;
	float B= Y - Co-Cg;
	return vec3(R,G,B);
}

//#define NO_AA

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;    
    vec3 new = RGBToYCoCg(textureLod(iChannel0, q, 0.0).xyz);
    vec3 history = RGBToYCoCg(textureLod(iChannel1, q, 0.0).xyz);

    vec3 colorAvg = new;
    vec3 colorVar = new*new;

    // Marco Salvi's Implementation (by Chris Wyman)
    for(int i = 0; i < 8; i++)
    {
        vec3 fetch = RGBToYCoCg(texture(iChannel0, ((fragCoord.xy+float(offsets[i])*0.2 )/iResolution.xy)).xyz);
        colorAvg += fetch;
        colorVar += fetch*fetch;
    }
    colorAvg /= 9.0;
    colorVar /= 9.0;
    float gColorBoxSigma = 0.75;
	vec3 sigma = sqrt(max(vec3(0.0), colorVar - colorAvg*colorAvg));
	vec3 colorMin = colorAvg - gColorBoxSigma * sigma;
	vec3 colorMax = colorAvg + gColorBoxSigma * sigma;

    history = clamp(history, colorMin, colorMax);

	fragColor = vec4(YCoCgToRGB(mix(new, history, 0.95)), 1.0);
#ifdef NO_AA
    fragColor = vec4(YCoCgToRGB(new), 1.0);
#endif
}

image_code:
/*
	Shader Sundays! (2/52) 
	"Cornell Box"

	This week I was	inspired by watching Luna break down Brutal Knowledge (https://www.shadertoy.com/view/3dyXzD)
	in this stream( https://www.youtube.com/watch?v=xAVLCoDc7qo&feature=youtu.be ). Honestly could not be happier
	with the results. GPU path tracing turned out to be much simpler than I expected and it looks great! 
	I got to reuse most of my raymarching knowledge from other SDF experiments so that really simplified things.
	The only real "new" stuff is the GI_BOUNCES loop in the render function.  

	For the future I want to dig into how the rayOnHemisphere function works and see if I can get some blue noise
	in here :D. 

	Other sources and references from IQ:
	https://www.shadertoy.com/view/3dyXzD //Basic Montecarlo 
	https://www.shadertoy.com/view/Xds3zN //SDF Primitives
*/

#define INV_GAMMA 0.454545

void
mainImage(out vec4 fragColor, in vec2 fragPos)
{
    //Camera setup
    vec2 uv = fragPos / iResolution.xy;
    vec3 col = vec3(0.0);

    //Time averaging previous frames
    if(iFrame > 0)
    {
        col = texture(iChannel0, uv).xyz;
        col /= float(iFrame);
    }

    //Postprocessing
    col *= 2.0;                                     // Exposure
    col = pow(col, vec3(INV_GAMMA));                // Gamma correction

    fragColor = vec4(col, 1.0);
}

// All the distance functions from:https://iquilezles.org/articles/distfunctions
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 200
#define MAX_DIST 100.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)

float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec4 combine(vec4 val1, vec4 val2 ){
    return (val1.w < val2.w)?val1:val2;
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
    return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdPyramid( vec3 p, float h)
{
  float m2 = h*h + 0.25;

  p.xz = abs(p.xz);
  p.xz = (p.z>p.x) ? p.zx : p.xz;
  p.xz -= 0.5;

  vec3 q = vec3( p.z, h*p.y - 0.5*p.x, h*p.x + 0.5*p.y);

  float s = max(-q.x,0.0);
  float t = clamp( (q.y-0.5*p.z)/(m2+0.25), 0.0, 1.0 );

  float a = m2*(q.x+s)*(q.x+s) + q.y*q.y;
  float b = m2*(q.x+0.5*t)*(q.x+0.5*t) + (q.y-m2*t)*(q.y-m2*t);

  float d2 = min(q.y,-q.x*m2-q.y*0.5) > 0.0 ? 0.0 : min(a,b);

  return sqrt( (d2+q.z*q.z)/m2 ) * sign(max(q.z,-p.y));
}

// https://www.shadertoy.com/view/3sd3Rs
float bnoise( in float x )
{
    // setup    
    float i = floor(x);
    float f = fract(x);
    float s = sign(fract(x/2.0)-0.5);

    // use some hash to create a random value k in [0..1] from i
    //float k = hash(uint(i));
  	//float k = 0.5+0.5*sin(i);
  	float k = fract(i*.1731);

    // quartic polynomial
    return s*f*(f-1.0)*((16.0*k-4.0)*f*(f-1.0)-1.0);
}

float hash(in vec3 x) { return fract(sin(dot(x, vec3(12.9898, 78.233, 49.256)))*43237.5324); }

vec4 tree(vec3 q, float y) {
    float tree = sdBox(q-vec3(0.0,1.0+y,0.0),vec3(0.2,2.0,0.2));

    q.xz *= 0.6;
    float tree0 = sdPyramid(q-vec3(0.0,1.8+y,0.0), 3.0);
    q.xz *= 1.2;
    float tree1 = sdPyramid(q-vec3(0.0,3.1+y,0.0), 2.0);
    tree = min(tree,min(tree0,tree1));
    vec4 res = vec4(vec3(0.8),tree);
    return res;
}

vec4 GetDist(vec3 p) {

    vec3 prevP = p;
    float _floor = p.y;

    float y = -0.5;
    float t = iTime*2.0;

    // ground
    p.x +=t;
    p += bnoise( p.x*0.1)+bnoise( p.z*0.1);
    _floor = p.y;
    vec4 resF = vec4(vec3(0.7),_floor*0.9);

    // tree
    p = prevP;
    p.x += t;

    vec3 q = p;

    vec3 c = vec3(20., 0., 5.0);
    vec3 id = floor(p / c) * vec3(1.0,0.0,1.0);
	vec4 q2 = vec4(mod(p, c) - 0.5*c, hash(id));
    float h = 1.0-q2.w*3.0;
    q.y -= (h<-0.5)?0.1:h;
    q = vec3(q2.x, q.y, q2.z);
    q.x -= 3.0*(q2.w*2.0-1.0);

    vec4 resTree = tree(q,y);

    vec4 model = combine(resTree,resF);
    return model;
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 dO= vec4(0.0);

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.w;
        vec4 dS = GetDist(p);
        dO.w += dS.w;
        dO.xyz = dS.xyz;
        if(dO.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = GetDist(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

float GetAmbientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - RayMarch(p + n*d,n).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

vec2 GetLight(vec3 p) {
    vec3 lightPos = vec3(2,8,3);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).w;

    float ao = GetAmbientOcclusion(p,n);

    float lambert = max(.0, dot( n, l))*0.1;

    float shadow = shadowMap(p + n * 0.001, l);

    return vec2((lambert+dif*ao),max(0.9, shadow)) ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 4, -5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.xz *= Rot(radians(-20.0));
    ro.yz *= Rot(radians(-20.0));
    #endif

    vec3 rd = R(uv, ro, vec3(0,1,0), 1.);

    vec4 d = RayMarch(ro, rd);

    if(d.w<MAX_DIST) {
        vec3 p = ro + rd * d.w;

        vec2 dif = GetLight(p);
        col = vec3(dif.x)*d.xyz;
        col *= dif.y;

    } else {
        // background
        col = vec3(1.0);
        float fullmoon = smoothstep(0.02,-0.02,length(uv-vec2(.6,.35))-0.08);
        col *= fullmoon;
    }

    fragColor = vec4(col,1.0);
}

const float EPSI = 0.01;

float SDF(vec3 p){

    float c = 99.;
    vec3 q = mod(p+0.5*c,c)-0.5*c;
	return length(q)-0.5;
}

float marcher(vec3 ro, vec3 rd){

	float totalDst = 0.;
    for(int i =0;i<100;i++){
    	vec3 p = ro+totalDst*rd;
        float dst = SDF(p);
        totalDst+=dst;
        if(totalDst>1000. || dst < EPSI)break;
    }
    return totalDst;
}

vec3 normal(vec3 p ){

	vec3 norm = normalize(vec3(SDF(vec3(p.x+EPSI,p.y,p.z))-SDF(vec3(p.x-EPSI,p.y,p.z)),
                               SDF(vec3(p.x,p.y+EPSI,p.z))-SDF(vec3(p.x,p.y-EPSI,p.z)),
                               SDF(vec3(p.x,p.y,p.z+EPSI))-SDF(vec3(p.x,p.y,p.z-EPSI))));
    return norm;
}

float lighting(vec3 p){

    vec3 lightPos= vec3(1,4,9);
    vec3 l = normalize(lightPos-p);
    vec3 n = normal(p);
    float diff = clamp(dot(n,l),0.,1.);
	return diff;
}

