    vec2 uv =(fragCoord-.5*iResolution.xy)/iResolution.x;
    vec3 ro = vec3(40.+(0.*iTime),70.*iTime,70.*iTime);
    vec3 rd = normalize(vec3(uv,1));
    vec3 col = vec3(lighting(ro+rd*vec3(marcher(ro,rd))));
    fragColor = vec4(col,1.0);
}

#define pi 3.14159

float smoothEquality(float a, float b, float sharpness){
    return exp(-abs(a-b)*sharpness);
}

float pInfiniteLength(vec2 a){
    return  max(abs(a.x),abs(a.y));
}

/*
	h : given in radians.
	s : from 0 to 1.
	v : from 0 to 1.
*/
vec3 hsvToRgb(vec3 hsv){/* disgusting (and not understood) but from wikipedia :
						https://fr.wikipedia.org/wiki/Teinte_Saturation_Valeur */
    float s = hsv.y;
    float v = hsv.z;


    vec3 rgb;
    hsv.x *= 180.f/pi;
    hsv.x = mod(hsv.x,360.f);
    int state = int(hsv.x/60.f)%6;
    float f = hsv.x/60.f - float(state);

    float l = (1.f - s);
    float m = (1.f - s * f);
    float n = (1.f - (1.f-f) * s);

    return v * vec3[](vec3(1,n,l),
                 vec3(m,1,l),
                 vec3(l,1,n),
                 vec3(l,m,1),
                 vec3(n,l,1),
                 vec3(1,l,m))[state];
}

vec3 smoothedCircle(vec2 xy, vec2 center,
                    float radius, float sharpness){
    xy/=radius;
    return vec3(hsvToRgb(vec3(pi + atan(xy.y,xy.x),1.0f,
                              smoothEquality(length(xy-center),radius,sharpness))));
}


