#define PREVIOUS_BUFFER (iChannel0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec2 cuv = uv - 0.5;
    cuv.x *= iResolution.x / iResolution.y;

    float t1 = iTime + sin(iTime * 1.3);

    vec3 color = vec3(0.0);
    // float acc = 0.0;

    const int samples = 33;

    // More blur (and noise) the further off we go from the center
    float offsetStrength = 0.001 + 0.007 * (length(cuv)) * (0.6 + 0.4 * sinf(t1));

    for (int i = 0; i < samples; i++) {
        vec3 randomDirection = randomPointOnHemisphere(vec3(0.0, 0.0, 1.0), float(i));

        vec2 uvOffset = randomDirection.xy * offsetStrength;
        // float gauss = exp(-2.0 * (1.0 - randomDirection.z));

    	vec3 col = texture(PREVIOUS_BUFFER, uv + uvOffset).rgb; // * gauss;

        // help this definitely isn't how you do chromatic abberation
        int compId = i % 3;
        color[compId] += col[compId];

        // color += col;
        // acc += gauss;
    }

    // color /= acc;
    color /= float(samples) / 3.0;

    // Color correction
    color = pow(color, vec3(0.85, 0.95, 1.1));

    // Subtle vignette
    color *= 1.0 - 0.5 * pow(offsetStrength * 50.0 + length(cuv) * 0.15, 2.0);

    // Grain
    float lum = 1.0 - luminance(color);
    color = mix(color, vec3(hash(uv * 10.0 + vec2(iTime, iTime + 1.0))), 0.13 * mix(lum, 1.0, offsetStrength * 25.0));

    fragColor = vec4(color, 1.0);
}

