// This generates the "final" image right before the fancy post processing.

#define NOISE_TEXTURE (iChannel1)
#define PREVIOUS_BUFFER (iChannel0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float t1 = iTime + sinf(iTime * 1.3 + sinf(iTime * 1.7)) * 0.3;
    float t2 = iTime + sinf(iTime * 1.7 + sinf(iTime * 1.9));

    vec2 uv = fragCoord / iResolution.xy - 0.5;

    // Everyone needs a bit of perspective.
    vec3 ray = vec3(uv, 1.0);
    ray = normalize(ray);

    vec2 shake = vec2(cosf(t1), sinf(t1)) * vec2(0.065, 0.032) * 1.5;

    uv = ray.xy;
    uv += shake;

    // The wibbly-wobbly ripple effect.
    vec2 noiseOrganic = 2.0 * texture(NOISE_TEXTURE, uv * vec2(0.2, 0.6) + hash(iTime)).rg - 1.0;
    uv += noiseOrganic * mix(0.0015, 0.005, pow(max(cosf(t2), 0.0), 4.0));

    vec2 cuv = uv;
    cuv -= shake * 0.5; // parallax! 1.0 is shake-independent, 0.0 is no parallax
    cuv.x *= 0.95 * iResolution.x / iResolution.y;

    // "bleh" is "size" read by someone with a Scottish accent, backwards.
    const float bleh = 0.45;

    float border = smoothstep(bleh + 0.045 + 2.0/iResolution.y, bleh + 0.045, length(cuv));
    float borderInner = smoothstep(bleh + 0.03, bleh + 0.03 + 2.0/iResolution.y, length(cuv));
    float borderInnerInner = smoothstep(bleh, bleh + 2.0/iResolution.y, length(cuv));

    // What? The "lens" actually make things smaller? Bad me, yeesh.
    uv *= (2.0 - borderInner) / 2.;

    uv += 0.5;

    float samples = texture(PREVIOUS_BUFFER, uv).r;

    // This is also a part of the derivative trick
    vec2 f = mod(fragCoord -.5, 2.);

    float v = texture(PREVIOUS_BUFFER, uv).y;
    v = ( v + texture(PREVIOUS_BUFFER, (uv * iResolution.xy -(1.-f))/iResolution.xy).w ) / 2.;
    // and it ends here.

    /*const*/ float edge_width = 0.008 * iResolution.y;

    // Finally divide by the derivative. IT TOOK ONLY THREE BUFFERS :' )
    samples /= v;
    samples = smoothstep(edge_width * 0.5, edge_width, samples);
    samples = mix(1.0 - samples, samples, borderInnerInner);

    float samplesFinal = 1.0 - mix(samples, 1.0, borderInnerInner * 0.92);
    float borderFinal = border * borderInner;

    vec3 background_color = vec3(0.85) + 0.15 * textureLod(NOISE_TEXTURE, uv * vec2(1.7, 1.0), 4.0).rrr; // rrrrrrr > _>
    vec3 foreground_color = vec3(0.85, 0.45, 0.45);
    vec3 border_color = vec3(0.25);

    vec3 color = mix(background_color, foreground_color, samplesFinal);
    color = mix(color, border_color, borderFinal);

    fragColor = vec4(color, 1.0);
}

#undef NOISE_TEXTURE
#undef PREVIOUS_BUFFER

