#define AA 1   // make this 1 is your machine is too slow

#define PI 3.1415926
#define DEG2RAD ((PI * 2.0) / 360.0)
//------------------------------------------------------------------

float sdPlane( vec3 p )
{
	return p.y-0.5;
}

float sdCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
//------------------------------------------------------------------

float opS( float d1, float d2 )
{
    return max(-d2,d1);
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec3 opRep( vec3 p, vec3 c )
{
    return mod(p,c)-0.5*c;
}


// Repeat only a few times: from indices <start> to <stop> (similar to above, but more flexible)
float pModInterval1(inout float p, float size, float start, float stop) {
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p+halfsize, size) - halfsize;
	if (c > stop) { //yes, this might not be the best thing numerically.
		p += size*(c - stop);
		c = stop;
	}
	if (c <start) {
		p += size*(c - start);
		c = start;
	}
	return c;
}


// Repeat around the origin by a fixed angle.
// For easier use, num of repetitions is use to specify the angle.
float pModPolar(inout vec2 p, float repetitions) {
	float angle = 2.0*PI/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions/2.0)) c = abs(c);
	return c;
}


//---------------------------------------------------------
vec3 rotateX(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(p.x, ca * p.y - sa * p.z, sa * p.y + ca * p.z);
}
vec3 rotateY(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca * p.x + sa * p.z, p.y, -sa * p.x + ca * p.z);
}
vec3 rotateZ(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca * p.x - sa * p.y, sa * p.x + ca * p.y, p.z);
}


#define XP 0.0
#define ZP 0.0

//------------------------------------------------------------------
// old alpha tower version...
const vec3 p01 = vec3(XP+0.0,5.95, ZP+0.0);

vec4 SampleDistTexture(vec2 texuv)
{
    texuv.y+=1.5;
    float aspect = iResolution.x/iResolution.y;
    texuv.y *= aspect*0.6;
    return texture(iChannel0,vec2(texuv.x, texuv.y)) + vec4(0.0, 0.0, 0.0, 0.00001);
}



// alpha tower remix
vec2 map( in vec3 pos )
{
	vec2 res = vec2( sdPlane(pos), 1.0 ); 			// floor (checker)
    pos.x -= 2.0;
    //pos.x += sin(iTime*1.2)*2.0;
    //pos.z += cos(iTime*1.2)*2.0;
    pos = rotateY(pos,-iTime*0.4);
    pModPolar(pos.xz,2.);
    pos.y *= 0.5;
    pos.y += 2.;

    //pos.x -= 1.0+sin(iTime*0.718)*1.0;

	vec3 r1;

    r1 = pos-p01;
    float d3 = sdCylinder(r1,vec2(3.95,5.0));
    float d1 = sdCylinder(r1,vec2(4.0,6.0));
	vec4 _c = SampleDistTexture( vec2(atan(r1.x,r1.z), r1.y) / PI);
    float sDistField = _c.w - 0.5;//+1.0/256.0;
    d1 = max(sDistField,d1);
    d1-= 0.025;
    d1*= 0.5;
    float cval = iTime+47.+(pos.y*2.5);
    d1 = opS(d1,d3);
    res = opU(res, vec2( d1, cval)  );
	return res;
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 30.0;

    float t = tmin;
    float m = -1.0;
    for( int i=0; i<100; i++ )
    {
	    float precis = 0.0005*t;
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}


float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		col = 0.45 + 0.35*sin( vec3(0.05,0.08,0.10)*(m-1.0) );
        if( m<1.5 )
        {
            float f = mod( floor(0.5*pos.z) + floor(0.5*pos.x), 2.0);
            f+= 0.4;
            col = 0.1 + 0.9*f*vec3(0.45,0.55,0.35);
        }
#if 1
        // lighting        
        float occ = calcAO( pos, nor );
		vec3  lig = normalize( vec3(-0.4, 0.7, -0.6) );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, ref.y );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
		float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),4.0);

        dif *= softshadow( pos, lig, 0.01, 2.5 );
        dom *= softshadow( pos, ref, 0.01, 2.5 );

		vec3 lin = vec3(0.0);
        lin += 1.30*dif*vec3(1.00,0.80,0.55);
		lin += 15.00*spe*vec3(1.00,0.90,0.70)*dif;
        lin += 0.40*amb*vec3(0.40,0.60,1.00)*occ;
        lin += 0.50*dom*vec3(0.40,0.60,1.00)*occ;
        lin += 0.50*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.25*fre*vec3(1.00,1.00,1.00)*occ;
		col = col*lin;
#endif
    	col = mix( col, vec3(0.05,0.05,0.05), 1.0-exp( -0.0001*t*t*t ) );
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mo = iMouse.xy/iResolution.xy;
    if (iMouse.z<0.5)
        mo = vec2((180.0 * DEG2RAD)/6.0,0.0);


    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif

		// camera	
        //vec3 ro = vec3( 6.5*cos(0.1*time + 6.0*mo.x), 1.0 + 2.0*mo.y, 0.5 + 6.5*sin(0.1*time + 6.0*mo.x) );

		float ang = 0.0 * DEG2RAD;        
        vec3 ro = vec3( 9.5*cos(ang+(6.0*mo.x)), 3. + 5.0*mo.y, 9.5*sin(ang+(6.0*mo.x)) );
        vec3 ta = vec3( 0.0, 1.0, 0.0 );
        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta, 0.0 );
        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,4.0) );

        // render	
        vec3 col = render( ro, rd );

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif


    fragColor = vec4( tot, 1.0 );
}

// Created by evilryu
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Based on kuvkar's Windyplains: https://www.shadertoy.com/view/ltXXRM

#define GRASS_ENV 0.2
#define FREQ 340.
#define PI 3.1415926535

float hash11(float p)
{
    vec2 p2 = fract(vec2(p * 5.3983, p * 5.4427));
    p2 += dot(p2.yx, p2.xy + vec2(21.5351, 14.3137));
    return fract(p2.x * p2.y * 95.4337)*0.5+0.5;
}

vec3 hash33(float n) { return fract(sin(vec3(n,n+7.3,n+13.7))*1313.54531); }
vec3 noise(float x)
{
    float p=floor(x);
    float f=fract(x);
    f=f*f*(3.0-2.0*f);
    return mix(hash33(p+0.0), hash33(p+1.0), f);
}

float noise(vec2 x)
{
    vec2 p=floor(x);
    vec2 f=fract(x);
    f=f*f*(3.0-2.0*f);
    float n=p.x + p.y*57.0;
    return mix(mix(hash11(n+0.0), hash11(n+1.0),f.x),
               mix(hash11(n+57.0), hash11(n+58.0),f.x),f.y);
}

float fbm(vec2 p)
{
    float f=0.0;
    f+=.5*noise(p); p=p*2.01;
    f+=.25*noise(p); p=p*2.1;
    f+=.125*noise(p); p=p*2.03;
    return f;
}

float terrain(vec2 p) 
{
    float w=0.;
    float s=1.;
    p.x*=20.;
    w+=sin(p.x*.3521)*4.;
    for (int i=0; i<5; i++) 
    {
        p.x*=1.53562;
        p.x+=7.56248;
        w+=sin(p.x)*s;      
        s*=.5;
    }
    w=w*.5+.5;
    return smoothstep(0.,0.05,p.y-w*.01-0.02);
}

vec3 renderTerrian(vec3 ro, vec3 rd, vec3 col)
{
    col=mix(col,vec3(0.05,0.1,0.2)*2.7, 1.-terrain(rd.xy));

    return col;
}

float mapTerrian(vec3 p)
{
    vec2 q=p.xz*1.1-vec2(2.3,0.);
    float f=0.0;
    f+=.56*noise(q); q=q*2.01;
    f+=.25*noise(q); q=q*2.1;
    //f+=.125*noise(q); q=q*2.03;
    return f-0.3;
}

float traceTerrian(vec3 ro, vec3 rd)
{
    float t=0.01,d,h;
    float tPrev=t;
    vec3 p;
    float grassMint=100.;
    for(int i=0;i<200;++i)
    {
        p=ro+t*rd;
        h=mapTerrian(p);    
        d=p.y-h;

        if(p.y-h-GRASS_ENV < 0.001 && grassMint>99.)
        {
			grassMint=tPrev;   
            break;
        }

        if(t>=100.0) { break;}

        tPrev=t;
        t+=d;
    }

    return grassMint;
}

float blades[8];

void rotate(float v)
{
    blades[7]=blades[6]; blades[6]=blades[5]; blades[5]=blades[4];
    blades[4]=blades[3]; blades[3]=blades[2]; blades[2]=blades[1];
    blades[1]=blades[0]; blades[0]=v;
}

bool useAA=false;
float oldD=0.;

float shear(vec3 p, float t)
{
    return sin(t+p.x*3.+p.z*(2.))*.3*(p.y)*(1.-smoothstep(-1.5, 2., p.z));
}

float grassNoise(vec2 p)
{
    vec2 l=floor(p);
    vec2 f=fract(p);
    f=f*f*(3.-2.*f);
    float n=l.x+l.y*57.;
    float d=mix(mix(hash11(n+0.0), hash11(n+1.0),f.x),mix(hash11(n+57.0), hash11(n+58.0),f.x),f.y);
    return d;
}

float mapGrass(vec3 p)
{
    vec3 q=p;
    // Shear
    p.xy=vec2(p.x+shear(p,0.)+.02*shear(p,iTime*2.), p.y);
    float d=grassNoise(p.xz*FREQ);
    d*=mix(1.,grassNoise(p.xz*30.),0.6);

    #if 1
    if(useAA)
    {
        rotate(d);
        d = (blades[0]+blades[1]+blades[2]+blades[3]
            +blades[4]+blades[5]+blades[6]+blades[7])*0.125;
    }
    #else
    if(useAA&&oldD>0.)
    {
        d=(oldD+d)*.5;
    }
    oldD=d;
    #endif

    d*=GRASS_ENV;
    return d;
}

float occlusion(vec3 p)
{
    // 1 cell backwards
 	float w=1./FREQ;
    // check the difference of height
    float d=p.y-mapGrass(p-vec3(0,0,w))-mapTerrian(p);
    // normalize
    return clamp(d/GRASS_ENV, 0.0, 1.0);
}

float bisect(vec3 ro, vec3 rd, float near, float far)
{
    float mid=0.;
    vec3 p=ro+near*rd;
    float sgn=sign(p.y-mapGrass(p)-mapTerrian(p));
    for (int i=0; i<6; i++)
    { 
        mid=(near+far)*.5;
        p=ro+mid*rd;
        float d=p.y-mapGrass(p)-mapTerrian(p);
        if(abs(d)<0.001)break;
        d*sgn<0. ? far=mid : near=mid;
    }
    return (near+far)*.5;
}

vec4 renderGrass(vec3 ro, vec3 rd, float mint)
{
	float t=mint;//((ro.y-GRASS_ENV)/abs(rd.y));

    vec3 p=ro+t*rd;
    float d=p.y-mapGrass(p)-mapTerrian(p);
	float sgn=sign(d);
    float told=0.;
	bool doBisect=false;

    useAA=true;

    for(int i=0;i<500;++i)
    {
        d=p.y-mapGrass(p)-mapTerrian(p);
        if (sign(d)!=sgn)
        {
            doBisect=true;
            break;
        }

        if(d<0.003||t>=50.0)
        {
            break;
        }

        told=t;

	    d=max(1e-4, 0.04*d*exp(t*.2));
        p+=d*rd;
        t+=d;
    }
    if (doBisect)t=bisect(ro,rd,told,t);

    useAA=false;
    vec3 col=vec3(0.1,0.5,1.)*3.5;
    col=renderTerrian(ro,rd,col);

    if(t<50.)
    {
        vec3 ld0=normalize(vec3(5., 5.0, 0.));
        col=vec3(0.1,0.2,0.05)*10.;
        col=mix(col,vec3(0.12,0.1,0.05)*10.,fbm(p.xz*2.));       
        col*=smoothstep(GRASS_ENV*0.45,GRASS_ENV*2.,p.y-mapTerrian(p));
        col*=pow(smoothstep(0.,1.,fbm(p.xz)),2.);

        col*=.7*smoothstep(0.3,0.5,p.y)+.3;

        col*=.8*occlusion(p)+.2;

        col*=20.;
    	col=mix(col,vec3(0.05,0.1,0.2)*2.7, 1.0-exp(-0.01*t*t));
    }

    return vec4(col,t);
}

vec3 tonemap(vec3 x) 
{
    const float a=2.51, b=0.03, c=2.43, d=0.59, e=0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

vec3 firecolor(float f)
{
	f=f*f*(3.-2.*f);
    return min(vec3(f+.8, f*f*1.4+.1, f*f*f*.7)*f, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;
	if(abs(p.y)-0.85>0.) { fragColor.xyz=vec3(0); return; }

    vec3 lookat=vec3(0.0, 0., 0.0);
	vec3 ro=vec3(0., .8,-2.5);
    ro.xy+=vec2(0.04,0.01)*noise(iTime*0.5).xy;

    vec3 forward=normalize(lookat-ro);
    vec3 right=normalize(cross(forward, vec3(0.0, 1.0, 0.0)));
    vec3 up=normalize(cross(right, forward));

    vec3 rd=normalize(p.x*right + p.y*up + 1.6*forward);

    vec3 col=vec3(0.1,0.5,1.)*0.1;

    float grassMint=traceTerrian(ro,rd);
	vec4 res=renderGrass(ro,rd,grassMint);
    col=res.xyz;
    col+=firecolor(0.5)*pow(clamp(dot(normalize(vec3(.0, 1., 2.)),rd), 0.0, 1.0),10.);
    col=tonemap(col);
    col=pow(clamp(col,0.0,1.0),vec3(0.45));
    col=col*0.6+0.4*col*col*(3.0-2.0*col);
    //col=pow(col,vec3(0.85,0.9,1.));
    col*=pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.2);
    fragColor.xyz=col;
}

// Plento
vec2 R;

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float sdRbox( vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - .05;
}

float minDist = 999.;
float mat = -1.;

void cd(float tDist, float m, out float tmat){
	if(tDist <minDist){
		minDist = tDist;
		mat = m;
		tmat = m;
	}
}

vec2 map(vec3 rp){
    float d = 999.;
    float hmat = -1.;

    rp.xy *= rot(rp.z*.15);
    rp.xz *= rot(rp.z*.17);

    rp.z += iTime;

    vec3 b = vec3(0., 0., .7);
    vec3 rpr = mod(rp, b)-b*.5;

    d = min(d, sdRbox(rpr, vec3(1.25, .15, .3)));
    cd(d, .0, hmat);

    rpr = mod(rp-vec3(.6, 0.07, .35), b)-b*0.5;

    if( ((int(round(rp.z/.7))%7)&3)!=0 )// Iq's fix
    	d = min(d, sdRbox(rpr, vec3(.65, .2, .05)));

    cd(d, 1., hmat);

    rpr = mod(rp-vec3(1.9, 0., .0), b)-b*.5;

    return vec2(d, hmat);
}

vec3 normal( in vec3 pos ){
    vec2 e = vec2(0.002, -0.002);
    return normalize(
        e.xyy * map(pos + e.xyy).x + 
        e.yyx * map(pos + e.yyx).x + 
        e.yxy * map(pos + e.yxy).x + 
        e.xxx * map(pos + e.xxx).x);
}

vec2 march(vec3 rd, vec3 ro){
 	float t = 0.;   
    vec2 d = vec2(0);

    for(int i = 0; i < 80; i++){
    	d = map(ro + rd*t); 	   
        if(abs(d.x) < .002 || t > 90.){
            break;
        }
        t += d.x * .75;
    }   
    return vec2(t, d.y);
}

vec3 color(vec3 p, vec3 rd, vec3 n, vec2 u, float t, float m){
    vec3 lp = vec3(0., 14., -5.);
    vec3 ld = normalize(lp-p);
    vec3 ref = reflect( rd, n );

    float lgd = length(lp - p);
    float faloff = 1.-exp(-(4. / lgd));

    float fres = smoothstep(-.5, .5, dot(rd, n));
    float spec = pow(max(dot(normalize(reflect(ld, n)), rd), 0.), 12.);
    float dom = smoothstep(-.2, .2, ref.y);
    float dif = max(dot(n, ld), .0);

    vec3 col = vec3(0);

    if(m == 0.)col = vec3(.9);
    if(m == 1.)col = vec3(.03);   

    vec3 lig = vec3(0);

    lig += .4*fres*vec3(1);
    lig += .25*dom*vec3(.2,.4,1.)*dom;
    lig += 2.*dif*vec3(1., 1., 1.) * faloff;
    col *= lig;
    col += vec3(.75, .8, .8) * spec * .45;

    col *= smoothstep(.21, 0., t/100.);

	return col;   
}
void mainImage( out vec4 f, in vec2 u ){
    R = iResolution.xy;
    vec2 uv = vec2(u.xy - .5*R.xy)/R.y;
    vec2 m = iMouse.xy / R.xy-.5; 
    m.x *= R.x/R.y;

    vec3 rd = normalize(vec3(uv, .6));
    rd = normalize(vec3(uv, 1. - dot(uv, uv) * .5));
    vec3 ro = vec3(-2.6, .65, -.55);

    rd.xz*= rot(-1.45);
    rd.yz *= rot(.6);

    vec2 t = march(rd, ro);

    vec3 n = normal(ro + rd*t.x);
    vec3 col = color(ro + rd*t.x, rd, n, u, t.x, t.y);

    f = vec4(sqrt(clamp(col, .0, 1.)), 1.);
}

#define PI 3.14159265359
#define PHI 1.61803398875

#define SAMPLES 5
#define BLOOM_RADIUS 30.0

const mat3 ACESInputMat = mat3(
    0.59719, 0.35458, 0.04823,
    0.07600, 0.90834, 0.01566,
    0.02840, 0.13383, 0.83777
);

const mat3 ACESOutputMat = mat3(
     1.60475, -0.53108, -0.07367,
    -0.10208,  1.10813, -0.00605,
    -0.00327, -0.07276,  1.07602
);

vec3 RRTAndODTFit( in vec3 v ) {
    vec3 a = v * (v + 0.0245786) - 0.000090537;
    vec3 b = v * (0.983729 * v + 0.4329510) + 0.238081;
    return a / b;
}

vec3 ACESFitted( in vec3 color ) {
    color = color * ACESInputMat;
    color = RRTAndODTFit(color);
    color = color * ACESOutputMat;
    color = clamp(color, 0.0, 1.0);
    return color;
}

vec3 hash33(vec3 p3) {
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec3 bloom = vec3(0);
    float totfac = 0.0;

    vec3 rnd = hash33(vec3(fragCoord, iFrame));
    float offset = rnd.x*2.0*PI;

    // bloom
    for (int i = 0 ; i < SAMPLES ; i++) {
        float theta = 2.0*PI*PHI*float(i) + offset;
        float radius = sqrt(float(i)) / sqrt(float(SAMPLES));
        radius *= BLOOM_RADIUS;
        vec2 offset = vec2(cos(theta), sin(theta))*radius;
        vec2 delta = vec2( 1.0+exp(-abs(offset.y)*0.1) , 0.5);
        offset *= delta;
        vec4 here = textureGrad(iChannel0,(fragCoord+offset)/iResolution.xy, 
                                vec2(0.001, 0)*BLOOM_RADIUS, vec2(0, 0.001)*BLOOM_RADIUS);
        float fact = smoothstep(BLOOM_RADIUS, 0.0, radius);
        bloom += here.rgb*0.05*fact;
        totfac += fact;
    }

    bloom /= totfac;

    vec2 uv = fragCoord/iResolution.xy;
    vec2 mo = uv*2.0-1.0;
    mo *= 0.01;
    fragColor.r = textureLod(iChannel0, uv-mo*0.1, 0.0).r;
    fragColor.g = textureLod(iChannel0, uv-mo*0.6, 0.0).g;
    fragColor.b = textureLod(iChannel0, uv-mo*1.0, 0.0).b;

    fragColor.rgb += bloom*bloom*100.0;
    vec2 vi = fragCoord / iResolution.xy * 2.0 - 1.0;
    fragColor.rgb *= (1.0-sqrt(dot(vi,vi)*0.45));
    fragColor.rgb = ACESFitted(fragColor.rgb);
    fragColor.rgb = pow( fragColor.rgb, vec3(1.0/2.2) );
    fragColor.rgb += (rnd-0.5)*0.1;

}

