// --- Gabor params & utils

float F = 1.8;                     // frequency
#define W 12.                      // filtering neighborhood
#define N int(W*2.+0.)             // sampling = NxN   ( N=2W+1 but smaller may work )
#define PI 3.14159
#define TG 1.*iTime                // try 1
#define CS(a)       vec2( cos(a+TG), sin(a+TG) )
#define hash2(p)    fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)
#define cmul(A,B) ( mat2( A, -(A).y, (A).x ) * (B) )  // by deMoivre formula

