		// Check whether we hit something
		vec3 backColor = vec3 (0.0, 0.0, 0.1 + 0.2 * max (0.0, dot (-direction, lightDirection)));
		if (dist >= 0.0) {
			fragColor = fragColor * (1.0 - intensity) + backColor * intensity;
			break;
		}

		// Get the normal
		vec3 normal = normalize (distanceFactor * vec3 (
			getDistance (origin + delta.xyy) - getDistance (origin - delta.xyy),
			getDistance (origin + delta.yxy) - getDistance (origin - delta.yxy),
			getDistance (origin + delta.yyx) - getDistance (origin - delta.yyx)));

		// Basic lighting
		vec3 reflection = reflect (direction, normal);
		if (distanceFactor > 0.0) {
			float relfectionDiffuse = max (0.0, dot (normal, lightDirection));
			float relfectionSpecular = pow (max (0.0, dot (reflection, lightDirection)), SPECULAR_POWER) * SPECULAR_INTENSITY;
			float fade = pow (1.0 - rayLength / RAY_LENGTH_MAX, FADE_POWER);

			vec3 localColor = max (sin (k * k), 0.2);
			localColor = (AMBIENT + relfectionDiffuse) * localColor + relfectionSpecular;
			localColor = mix (backColor, localColor, fade);

			fragColor = fragColor * (1.0 - intensity) + localColor * intensity;
			intensity *= REFRACT_FACTOR;
		}

		// Next ray...
		vec3 refraction = refract (direction, normal, refractionRatio);
		if (dot (refraction, refraction) < DELTA) {
			direction = reflection;
			origin += direction * DELTA * 2.0;
		}
		else {
			direction = refraction;
			distanceFactor = -distanceFactor;
			refractionRatio = 1.0 / refractionRatio;
		}
	}

	// Return the fragment color
	return fragColor * LUMINOSITY_FACTOR + GLOW_FACTOR * rayStepCount / float (RAY_STEP_MAX * RAY_COUNT);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord
   ){

	// Define the ray corresponding to this fragment
	vec2 frag = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    ;frag*=.3;
	vec3 direction = normalize (vec3 (frag, 2.0));

	// Set the camera
	vec3 origin = vec3 ((15.0 * cos (iTime * 0.1)), 10.0 * sin (iTime * 0.2), 15.0 * sin (iTime * 0.1));
	vec3 forward = -origin;
	vec3 up = vec3 (sin (iTime * 0.3), 2.0, 0.0);
	mat3 rotation;
	rotation [2] = normalize (forward);
	rotation [0] = normalize (cross (up, forward));
	rotation [1] = cross (rotation [2], rotation [0]);
	direction = rotation * direction;

	// Set the fragment color
	fragColor = vec4 (getFragmentColor (origin, direction), 1.0);
}

// Fork of "flow by Perlin noise"     https://shadertoy.com/view/Xl3Gzj
//   and "Moebius Spiral Gabor field" https://shadertoy.com/view/wtt3RH
// ( see also https://www.shadertoy.com/view/ldtSzn
//            https://shadertoy.com/view/wtt3z8 
//            https://shadertoy.com/view/wtcGRH )
// see also: distance field variant https://www.shadertoy.com/view/WtK3DR

// --- Gabor params & utils

float F = 1.2;                     // frequency
#define W 12.                      // filtering neighborhood
#define N int(W*2.+0.)             // sampling = NxN   ( N=2W+1 but smaller may work )
#define PI 3.14159
#define TG 0.*iTime                // try 1
#define CS(a)       vec2( cos(a+TG), sin(a+TG) )
#define hash2(p)    fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)
#define cmul(A,B) ( mat2( A, -(A).y, (A).x ) * (B) )  // by deMoivre formula

// --- flow params

#define TF .2*iTime                // flow speed
float S = 3.;                      // scaling of flow


// --- Perlin noise by inigo quilez - iq/2013   https://www.shadertoy.com/view/XdXGW8
//     (extended to 3D)
vec3 hash( vec3 p )
{
	p *= mat3( 127.1,311.7,-53.7,
			   269.5,183.3, 77.1,
			  -301.7, 27.3,215.3 );

	return 2.*fract(sin(p)*43758.5453123) -1.;
}

float noise( vec3 p )
{
    vec3 i = floor( p ),
         f = fract( p ),
	     u = f*f*(3.-2.*f);

    return 2.*mix(
              mix( mix( dot( hash( i + vec3(0,0,0) ), f - vec3(0,0,0) ), 
                        dot( hash( i + vec3(1,0,0) ), f - vec3(1,0,0) ), u.x),
                   mix( dot( hash( i + vec3(0,1,0) ), f - vec3(0,1,0) ), 
                        dot( hash( i + vec3(1,1,0) ), f - vec3(1,1,0) ), u.x), u.y),
              mix( mix( dot( hash( i + vec3(0,0,1) ), f - vec3(0,0,1) ), 
                        dot( hash( i + vec3(1,0,1) ), f - vec3(1,0,1) ), u.x),
                   mix( dot( hash( i + vec3(0,1,1) ), f - vec3(0,1,1) ), 
                        dot( hash( i + vec3(1,1,1) ), f - vec3(1,1,1) ), u.x), u.y), u.z);
}

float Mnoise(vec3 U ) {
    return noise(U);                      // base turbulence
  //return -1. + 2.* (1.-abs(noise(U)));  // flame like
  //return -1. + 2.* (abs(noise(U)));     // cloud like
}

float turb( vec2 U, float t )
{ 	float f = 0., q=1., s=0.;

    float m = 2.; 
 // mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    for (int i=0; i<2; i++) {
      U -= t*vec2(.6,.2);
      f += q*Mnoise( vec3(U,t) ); 
      s += q; 
      q /= 2.; U *= m; t *= 1.71;  // because of diff, we may rather use q/=4.;
    }
    return f/s; 
}
// -----------------------------------------------

// draw line segment
#define L(a,b) O+= .3/R.y/length( clamp( dot(U-(a),v=b-(a))/dot(v,v), 0.,1.) *v - U+a )

// === Gabor noise =============

vec2 Gabor(vec2 U, vec2 V) {
    vec2 P, v, s = vec2(0);
    float T=0., K;    
    for( int k = 0; k < N*N; k++) {            // Gabor noise: convolve with noise with K*oscill
        P = vec2( k%N, k/N ) -  W;             // sample neighborhood ( W = float(N-1)/2. )
      //v = CS( 2.*PI* hash2( U+P ));          // random complex signal
      //v = cmul( v, CS(-F*dot(P,V)));         // mul by oscillator in main direction
        v = CS( 2.*PI* hash2( U+P )            // random complex signal
                - F* dot(P,V) );               // mul by oscillator in main direction
        K = .5+.5*cos(PI*min(1.,length(P)/W)); // smoothing kernel
        s += v * K;
        T += K;
    }
    return s / T; 
}

// ========================================================

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u/R.y;
    O -= O;
                                        // --- generates flow
	float eps = 1e-3;
    //U -= t*vec2(.3,.1);                      // translation (or do it per band)

    float n = turb(S*U,TF);                     // pure noise = stream

                                               // flow = rot(stream) 
	vec2  V = vec2( turb(S*U+vec2(0,-eps),TF) - turb(S*U+vec2(0,eps),TF),
                    turb(S*U+vec2(eps,0),TF)  - turb(S*U+vec2(-eps,0),TF)
                  )/ eps;
    //V += vec2(3,0);                          // linearly combine other base flows
    V /= R.y;

#if 0                                   // --- draw flow ( debug )
  //O += pow(.5+.5*sin(100.*n),1./2.2); return;
	O = clamp(vec4(n,0,-n,0),0.,1.);           // draw stream value (note that curl = lapl(stream) so they are very similar)
    S = R.y/10.;
    vec2 p = floor(U*S+.5)/S, v;               // draw velocity vectors
    L ( p-V*2., p+V*2.);  //O.rb = u-u;  
    //return;
#endif
    O += .2*vec4(n,0,-n,0);                    // show stream ( for debug )

    vec2 M = iMouse.xy;                 // --- draw Gabor/Phasor noise
    if (length(M)>10.) F = .2+M.y/R.y*1.8; 
    V = normalize(V);  // direction only
  //V *= R.y;          // variant: varying length
#if 1
    if (fract(iTime/3.8)>.5) V = vec2(-V.y,V.x); // normal field
    vec2 s = Gabor(u -.5*R, V );
    float T = 
         //   4.*length(s);                    // show contrast oscillations
              .5+.5*normalize(s).x;            // phasor profile
 // T = pow( T, 1./2.2);                       // to sRGB
    O += T;
#else                                          // local grid
    O.r = .5+.5*normalize(Gabor(u -.5*R, V )).x;
    V = vec2(-V.y,V.x); 
    O.g = .5+.5*normalize(Gabor(u -.5*R, V )).x;
#endif
}

// Improved smoothing using smoothstep
// and mixing trick from 
// https://www.shadertoy.com/view/WdBGzd

#define PI2 6.28318530718

#define COLOR1 vec3(1., 1., 0.6)
#define COLOR2 vec3(0., 0.1, 0.4)
#define LIGHTCOLOR  vec3(1., 1., 1.)

#define ARMS 10.
#define DENSITY 3.
#define ANGLE_SPEED 1.
#define SPEED 2.
#define PULSE_SPEED 4.
#define SMOOTH 0.08

float smoothCheckerboard(in vec2 uv) {    
    // Shift, as fract gives 0..1 and we will smooth
    // around 0.25 and 0.75.
    uv = fract(uv + 0.25);
    float sm2 = SMOOTH * 0.5;
    // p01 oscillates between 0 and 1.
    vec2 p01 =
        smoothstep(0.25 - sm2, 0.25 + sm2, uv) -
        smoothstep(0.75 - sm2, 0.75 + sm2, uv);
    // pn11 oscillates between -1 and 1.
    vec2 pn11 = (p01 - 0.5) * 2.;
    // This results in smoothed 0..1 transitions.
    // We take advantage of the fact that multiplication
    // of -1/1 behaves like XOR. Then we rescale
    // -1..1 result of the multiplication to 0..1.
    return 0.5 - 0.5 * pn11.x * pn11.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2
        R = iResolution.xy,
        // Cartesian [-1, 1] along shorter axis.
        uv = (2. * fragCoord - R) / min(R.x, R.y);
    float
        T = iTime,
        // Angle [-PI, PI] and radius.
        a = atan(uv.x, uv.y),
        r = length(uv);

    // Rescale polar coords, as checkerboard generates 1x1 squares.
    vec2 polar = vec2(
        a * ARMS / PI2 + T * ANGLE_SPEED,
        1. / r * DENSITY + T * SPEED 
    );

    vec3 checker = mix(COLOR1, COLOR2, smoothCheckerboard(polar));
    // Light in the center.
    float light = smoothstep(0.7 + 0.3 * sin(T * PULSE_SPEED), 0.2, r);
    vec3 color = mix(checker, LIGHTCOLOR, light);
    fragColor = vec4(color, 1.);
}

// Sharpen kernel from https://www.shadertoy.com/view/MtdXW4
// There are also some parts of wyatt's framework left in the code.

// This is convolutional feedback (blur > sharpen feedback)
// The only thing I have added is some uv displacement


#define pal(x,t) (0.5 + 0.5*sin(x*vec3(0.67,0.2,0.7) + t))

void mainImage( out vec4 C, in vec2 U )
{ 
	#define grad 0.25*vec2(e.z-w.z,n.z-s.z)
    vec2 uv = (U-0.5*R)/R.y;

    vec4 CA = C * 1. - mix(0., 1., pow(length(uv)*1.1, 2.));
    Neighbors;
    vec4 a = A(U);
    C = B (U);
    vec3 no = normalize(vec3(grad,1.1));
    vec3 re = reflect(no,vec3(0.,0,1));
    float density = A(U).z;
    C = sin(0.2*density+0.2*pal(density*2. + no*3. + length(uv)*5. + iTime*2.,3.4).xyzy*density);

    //C = clamp(C, 0., 1.);
    C = pow(C, vec4(0.9,1.1,0.8,0.));
    C = smoothstep(0.,1.,C);
    C *= 7.5;


    C = mix(C, CA*pal(iTime*10. , 20.).xyxz*C.x, CA.x);
    //C = texture(iChannel1, U/R);
}

