//=======================================================
//= Solid Local Random Phase Noise - Simplex
//=======================================================
//== Computing a RandomPhaseNoise on a simplex grid
//== Need some cleaning... (artefacts, ...)
//=======================================================
//== Some great ressources for simplex noise :
//== *- Stefan Gustavson's "Simplex noise demystified"
//==    http://staffwww.itn.liu.se/~stegu/simplexnoise/simplexnoise.pdf
//== 
//== I also used some code from :
//== *- @nikat's commented implementation on Shadertoy 	<---  
//=   	https://www.shadertoy.com/view/XsX3zB			<--- 	
//== *- @candicat's implementation on Shadertoy
//==	https://www.shadertoy.com/view/4sc3z2
//==
//== Raymarching Code uses IQ's SDF ( Sphere & Box ) + Subtract Operator
//== https://iquilezles.org/articles/distfunctions
//==
//== Used also some code provided by Lagae et al. [2011]
//== "Filtering Solid Gabor Noise"
//== http://graphics.cs.kuleuven.be/publications/LD11FSGN/
//==
//== Feel free to use it ...
//==
//=======================================================
//== Author : Arthur Cavalier
//=======================================================



//-----------------------------------------------------------------------------
//-- User Parameters ----------------------------------------------------------
const uint  LRPN_GLOBAL_SEED 	= 0u;
const int   LRPN_COSINES 		= 5;
const float LRPN_SCALE_FREQ 	= 10.;
const float LRPN_SCALE_SPACE 	= 5.;
const float LRPN_MAGNITUDE 		= 1.;
#define MOUSE_CONTROL
//#define PHASE_SHIFT 


//-----------------------------------------------------------------------------
//-- Simplex Solid LRPN Code --------------------------------------------------
float gaussian_window_3D(in vec3 st, in float c, in float s)
{
    mat3 sig = mat3(s);
    float body = -0.5*dot(st,inverse(sig)*st);
    return c*exp(body);
}

float local_random_phase_noise(
    	in ivec3 vertex_index,
    	in vec3  noise_coords,
    	in int   nof_cosines,
    	in vec2  range_frequency,
        in vec2  range_orientation,
    	in float window_magni,
    	in float window_width
	)
{
    uint seed    = cell_seed(vertex_index,LRPN_GLOBAL_SEED);
    uint prng    = wang_hash(seed);

    float sc = 0.; 
    for(int k=0; k<nof_cosines; k++)
    {
        float fr = myrand_uniform_m_M(prng,range_frequency.x,range_frequency.y); 	 // pick a frequency 
        float or = myrand_uniform_m_M(prng,range_orientation.x,range_orientation.y); // pick an orientation
        float ph = myrand_uniform_0_1(prng)*m_2_pi; // pick a phase (shift)

	#ifdef PHASE_SHIFT
        ph = iTime;
	#endif

        float omega_r = fr;
        float omega_t = or;
        float omega_p = acos(myrand_uniform_0_1(prng)*2.-1.);
        vec3 F = omega_r * vec3(cos(omega_t) * sin(omega_p), sin(omega_t) * sin(omega_p), cos(omega_p));

        sc +=  cos(m_2_pi* dot(F, noise_coords) + ph);
    }
    return gaussian_window_3D(noise_coords,window_magni,window_width) * sc / float(nof_cosines);
}

float simplex_solid_random_phase_noise(
    	in vec3  P, // Evaluation Point
    	in int   C, // Number of Cosines
    	in vec2  F, // Frequency Range (freq_min ; freq_max) 
    	in vec2  O, // Orientation Range (theta_min ; theta_max)
    	in float K  // Gaussian Window Magnitude
	)
{
    const float F3 = 0.333333333; /* -> 1/3 */
    const float G3 = 0.166666667; /* -> 1/6 : distance edge-to-edge of the simplex grid */
    const float WW = 0.055555556; /* -> 1/6 * 1/3 : find the width of the gaussian window (3*\sigma ~ 0) */

    vec3 i = floor(P + (P.x + P.y + P.z) * F3);
    vec3 d0 = P - (i - (i.x + i.y + i.z) * G3);
    vec3 e = step(vec3(0.0), d0 - d0.yzx);
	vec3 i1 = e * (1.0 - e.zxy);
	vec3 i2 = 1.0 - e.zxy * (1.0 - e);

    vec3 d1 = d0 - (i1 - 1. * G3);
    vec3 d2 = d0 - (i2 - 2. * G3);
    vec3 d3 = d0 - (1. - 3. * G3);   

   	float n0 = local_random_phase_noise(ivec3(i + 0.), d0, C, F, O, K, WW);
    float n1 = local_random_phase_noise(ivec3(i + i1), d1, C, F, O, K, WW);
    float n2 = local_random_phase_noise(ivec3(i + i2), d2, C, F, O, K, WW);
    float n3 = local_random_phase_noise(ivec3(i + 1.), d3, C, F, O, K, WW);
    return(n0+n1+n2+n3);
}



//-----------------------------------------------------------------------------
//-- Raymarching Code ---------------------------------------------------------
vec3  world_get_background(in vec3 rd)       { return vec3(0.); }

float world_get_sdf(vec3 P)
{        
    float Sph = sdSphere(P,1.);
 	float Box = sdBox( P-vec3(0.,1.,0.) , vec3(2.,0.5+0.5*cos(0.5*iTime),2.));    

    float SDF = opSub(Box,Sph);
	return(SDF);
}

float world_raymarch(in vec3 ro, in vec3 rd, in float tmax)
{
    float step_count  = WORLD_STEPS;
    float depth_max   = tmax;
    float t = 0.01;
    float R = -1.;

    for(float istep=0.; istep<step_count && t<depth_max; istep+=1.)
    {
        float D = world_get_sdf(ro + t * rd);
        if( D < EPSILON )
        {
            R = t;
            break;
        }
        t += D;
    }
    return R;
}

vec3 world_get_normal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*world_get_sdf( pos + e.xyy ) + 
					  e.yyx*world_get_sdf( pos + e.yyx ) + 
					  e.yxy*world_get_sdf( pos + e.yxy ) + 
					  e.xxx*world_get_sdf( pos + e.xxx ) );  
}


vec3 raymarcher(vec3 ro, vec3 rd)
{
    vec3 col = vec3(0.);

    float hit_dis = world_raymarch(ro, rd, 10.);

    if(hit_dis<0.) 
    {
    	return world_get_background(rd);
    }


    int   C = LRPN_COSINES;
    vec2  F = vec2(0.3,1.)*LRPN_SCALE_FREQ; 
    vec2  O = vec2(0.,1.)*m_pi;
    float K = LRPN_MAGNITUDE;   

#ifdef MOUSE_CONTROL
    vec2 mouse = clamp(iMouse.xy / iResolution.xy, 0., 1.);
    F = vec2(0.3, max(0.1,mouse.x) )*LRPN_SCALE_FREQ;     
#endif


    vec3  hit_pos = ro+rd*hit_dis;
    vec3 coords = hit_pos * LRPN_SCALE_SPACE;
    float noise = simplex_solid_random_phase_noise(coords,C,F,O,K);
    return vec3( 0.5+0.5*noise );
}


//-----------------------------------------------------------------------------
//-- Main Render Function -----------------------------------------------------
vec3 render(in vec3 ro, in vec3 rd)
{
    vec3 color              = world_get_background(rd);
    vec2 scene_bouding_box  = intersection_axis_aligned_box(ro, rd, WORLD_BOUNDING_BOX);
    if(! (scene_bouding_box.x > scene_bouding_box.y || scene_bouding_box.y < 0.0)) 
    {
        vec3    B = ro + max(scene_bouding_box.x,0.) * rd;
        vec3    E = ro + scene_bouding_box.y * rd;
        vec3    V = rd;
        color = raymarcher(B,V);
    }
    return color;
}

//-----------------------------------------------------------------------------
//-- Main ---------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 screen_coords = ( 2.*(gl_FragCoord.xy) - iResolution.xy ) / iResolution.y;

    // IQ - Raymarching Primitives https://www.shadertoy.com/view/Xds3zN Camera
    vec3 camera_origin = vec3( 2.*cos(0.2*iTime) , 2.0 , 2.*sin(0.2*iTime) );
    vec3 camera_target = vec3(0.);
    vec3 ray_direction = compute_camera_ray_direction(screen_coords, camera_origin, camera_target, 0.);

    fragColor = vec4( render(camera_origin,ray_direction), 1.);   
}

