//this function is from https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy+sin(iTime));

}

vec2 magnify(vec2 fragCoord,float mag){
	return hash22(floor(fragCoord/pow(2.0,mag)));
}

vec2 pixel_above(vec2 fragCoord,float mag){
	return magnify(fragCoord+vec2(pow(2.0,mag),0),mag);
}

