#define FOCUS_FORMULA ((abs(texture(iChannel0,uv).w - FOCUS_DISTANCE)*BLUR_AMT ))
const float MULT = 4.0;

vec3 srgb2lin(vec3 c) { return c*c; }
vec3 lin2srgb(vec3 c) { return sqrt(c); }

float hash12n(vec2 p)
{
	p  = fract(p * vec2(5.3987, 5.4421));
    p += dot(p.yx, p.xy + vec2(21.5351, 14.3137));
	return fract(p.x * p.y * 95.4307);
}

vec3 sampletex( vec2 uv )
{
    #ifdef SRGBLIN
    	return srgb2lin( texture( iChannel0, uv, -10.0 ).rgb );
    #else
    	return  texture( iChannel0, uv, -10.0 ).rgb ;
    #endif
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec2 blurdir = vec2( 0.0, 1.0 );
    vec2 blurvec = normalize(blurdir) / iResolution.xy;
    vec2 uv = fragCoord / iResolution.xy;
    float blurdist = FOCUS_FORMULA;

    vec2 p0 = uv - 0.5 * blurdist * blurvec;
    vec2 p1 = uv + 0.5 * blurdist * blurvec;
    vec2 stepvec = (p1-p0) / float(NUM_SAMPLES);
    vec2 p = p0;
    #if defined(USE_RANDOM)
    p += (hash12n(uv+fract(iTime))-0.5) * stepvec;
    #endif

    vec3 sumcol = vec3(0.0);
    for (int i=0;i<NUM_SAMPLES;++i)
    {
        vec3 smpl = sampletex(p);
        sumcol += smpl;
        p += stepvec;
    }
    sumcol /= float(NUM_SAMPLES);
    sumcol = max( sumcol, 0.0 );


    #ifdef SRGBLIN
    	fragColor = vec4( lin2srgb( sumcol * MULT ), texture( iChannel0,uv).w );
    #else
    	fragColor = vec4( sumcol * MULT , texture( iChannel0,uv).w );
    #endif
}

