//by @etiennejcb
//3d simplex noise from https://www.shadertoy.com/view/XsX3zB

const float PI = 3.1415926535897932384626433832795;

/* discontinuous pseudorandom uniformly distributed in [-0.5, +0.5]^3 */
vec3 random3(vec3 c) {
	float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
	vec3 r;
	r.z = fract(512.0*j);
	j *= .125;
	r.x = fract(512.0*j);
	j *= .125;
	r.y = fract(512.0*j);
	return r-0.5;
}

/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
	 /* 1. find current tetrahedron T and it's four vertices */
	 /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
	 /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

	 /* calculate s and x */
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));

	 /* calculate i1 and i2 */
	 vec3 e = step(vec3(0.0), x - x.yzx);
	 vec3 i1 = e*(1.0 - e.zxy);
	 vec3 i2 = 1.0 - e.zxy*(1.0 - e);

	 /* x1, x2, x3 */
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.0*G3;
	 vec3 x3 = x - 1.0 + 3.0*G3;

	 /* 2. find four surflets and store them in d */
	 vec4 w, d;

	 /* calculate surflet weights */
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);

	 /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
	 w = max(0.6 - w, 0.0);

	 /* calculate surflet components */
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.0), x3);

	 /* multiply d by w^4 */
	 w *= w;
	 w *= w;
	 d *= w;

	 /* 3. return the sum of the four surflets */
	 return dot(d, vec4(52.0));
}


float transformValue(float v){
    v = 0.5 + 0.5*v;
    v = pow(v+0.35,7.0);
    return v;
}

//from bookofshaders
vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}

//  Function from Iigo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy/iResolution.x;
	float offset = 0.9*simplex3d(vec3(9.*p+vec2(0.,-0.3*iTime),0.2*iTime))*clamp((2.0-5.*length(p-vec2(0.5,0.25))),0.,2.) + 10.*length(p-vec2(0.5,0.25));
    //float offset2 = simplex3d(vec3(123)+vec3(10.*p+vec2(-1.*iTime,0),0.5*iTime));

    float change = 9.0;

    float scl = 5.;

    float value = simplex3d(vec3(change*(iTime-offset),scl*p.x,scl*p.y));
	float value2 = simplex3d(vec3(1.1*change*(iTime-offset),123.+scl*p.x,scl*p.y));
    float value3 = simplex3d(vec3(1.5*change*(iTime-offset),1234.+scl*p.x,scl*p.y));
    //float value4 = simplex3d(vec3(1.5*change*(iTime-offset),3234.+scl*p.x,scl*p.y));

	value = transformValue(value);
    value2 = transformValue(value2);
    value3 = transformValue(value3);

    vec3 color0 = vec3(value+value2,value2+0.*value3,value3+value);

    vec3 rgb = clamp(color0,0.,1.);

    vec3 hsb = rgb2hsb(rgb);

    hsb.x += 0.3*sin(13.*length(p-vec2(0.5,0.25))-1.*iTime)+0.15*iTime;

    hsb.x = mix(hsb.x,0.5,0.7);

    hsb.y += 0.2*sin(14.*length(p-vec2(0.5,0.25))-1.1*iTime+0.5)-0.4;

    rgb = hsb2rgb(hsb);

	fragColor = vec4(rgb,1.0);
	return;
}

/* "Gears using involute SDF" by Stanislav Pidhorskyi - 2020
 * License Creative Commons Attribution 4.0 International License.
 * Contact: stpidhorskyi@mix.wvu.edu
 */


float Union(float a, float b)
{
	return min(a, b);
}

float SmoothUnion(float a, float b, float k)
{
    float h = clamp(0.5 + 0.5*(b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float Substraction(float a, float b)
{
    return max(a,-b);
}

float Intersection(float a, float b)
{
    return max(a,b);
}

float SmoothIntersection(float a, float b, float k)
{
    return Substraction(Intersection(a, b), SmoothUnion(Substraction(a, b), Substraction(b, a), k));
}

float Circle(vec2 p, float r)
{
	return length(p) - r;
}

float Circle(float rho, float r)
{
	return rho - r;
}

float Gear(vec2 p, float D, float N, float psi, float alpha, float dir)
{
    float R = D / 2.0;
    /* The Pitch Circle Diameter is the diameter of a circle which by a pure rolling action would transmit 
     * the same motion as the actual gear wheel. It should be noted that in the case of wheels which connect
     * non-parallel shafts, the pitch circle diameter is different for each cross section of the wheel
     * normal to the axis of rotation.
     */

    float rho = length(p);

    float Pd = N / D; // The Diametral Pitch, p_d is the number of teeth per unit length of diameter

    float P = 3.1416 / Pd; // Circular Pitch
    /* The Circular Pitch, p is the length of arc round the pitch circle between corresponding points 
     * on adjacent teeth.
     */

    float a = 1.0 / Pd; // Addendum
    /* The Addendum is the radial length of a tooth from the pitch circle to the tip of the tooth.
     */

    float Do = D + 2.0 * a; // Outside Diameter
    float Ro = Do / 2.0; 

    float h = 2.2 / Pd;

    float innerR = Ro - h - 0.4;

    // Early exit
    if (innerR - rho > 0.0)
        return innerR - rho;

    // Early exit
    if (Ro - rho < -0.2)
        return rho - Ro;

    float Db = D * cos(psi); // Base Diameter 
    float Rb = Db / 2.0; 

    float fi = atan(p.y, p.x) * dir + alpha;

    float alphaStride = P / R;

    float invAlpha = acos(Rb / R);
    float invPhi = tan(invAlpha) - invAlpha;

    float shift = alphaStride / 2.0 - 2.0 * invPhi;

    float fia = mod(fi + shift / 2.0, alphaStride) - shift / 2.0;
    float fib = mod(-fi - shift + shift / 2.0, alphaStride) - shift / 2.0;

    float dista = -1.0e6;
    float distb = -1.0e6;

    if (Rb < rho)
    {
        float acos_rbRho = acos(Rb/rho);

        float thetaa = fia + acos_rbRho;
        float thetab = fib + acos_rbRho;

        float ta = sqrt(rho * rho - Rb * Rb);

        // https://math.stackexchange.com/questions/1266689/distance-from-a-point-to-the-involute-of-a-circle
        dista = ta - Rb * thetaa;
        distb = ta - Rb * thetab;
    }

    float gearOuter = Circle(rho, Ro);
    float gearBase = Circle(rho, Rb);
    float gearLowBase = Circle(rho, Ro - h);
    float crownBase = Circle(rho, innerR);

    float cogs = Intersection(dista, distb);

    float baseWalls = Intersection(fia - (alphaStride - shift), fib - (alphaStride - shift));

    cogs = Intersection(baseWalls, cogs);
    cogs = SmoothIntersection(gearOuter, cogs, 0.01);
    cogs = SmoothUnion(gearLowBase, cogs, Rb - Ro + h);

    cogs = Substraction(cogs, crownBase);
    return cogs;
}

float Alpha(float d)
{
    d *= iResolution.y / 2.0;
	return clamp(-d + 0.7, 0.0, 1.0);
}


float AO(float d, float height)
{
    return 1.0 - atan(height, d) / 3.14159;
}

float SDF(vec2 p)
{
    float N = pow(2.0, mod(floor(iTime / 15.0), 4.0)) * 4.0 + 9.0; // Number of Teeth
    float psi = 3.096e-5 * N * N -6.557e-3 * N + 0.551;// Pressure Angle
	/* The Pressure Angleor Angle of Obliquity  Psi is the angle 
 	 * between the common normal LM and the tangent at the pitch point
 	 */
    float D = 2.8;

    float gearA = Gear(p + vec2(1.5, 0.0), D, N, psi, iTime / 4.0, 1.0);
    float gearB = Gear(p + vec2(1.5 - D, 0.0), D, N, psi, iTime / 4.0, -1.0);
    float dist = Union(gearA, gearB);
    return dist;
}

vec2 normal(vec2 p, float dist)
{
	float dx = SDF(p + vec2(0.0, 1.0) / iResolution.yy) - dist;
	float dy = SDF(p + vec2(1.0, 0.0) / iResolution.yy) - dist;
    return normalize(vec2(dx, dy));
}

vec3 Render(vec2 p)
{
	// gradient
	vec3 color = vec3(0.55, 0.5, 0.45) * (1.3 - length(p / 2.0 / iResolution.xy * iResolution.yy));

	color = mix(color, vec3(0.1, 0.1, 0.2), 0.2 * Alpha(0.5 * SDF((p - vec2(1.0, 0.0)) / 2.0)));

    float dist = SDF(p);

	color *= AO(dist, 0.2);

	color = mix(color, vec3(0.1, 0.1, 0.1), Alpha(dist));

    #ifdef ADD_SPECULAR
    if (dist < 0.0)
    {
    	vec2 n = normal(p, dist);
        vec3 nn = normalize(vec3(n, -dist * 200.1));

        float s = dot(nn, normalize(vec3(1.0, 1.0, 1.0)));

        s = pow(max(s, 0.0), 20.0);

    	color += vec3(s) * clamp(-dist * iResolution.y / 4.0, 0.0, 1.0);
    }
    #endif

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 uv = (fragCoord.xy  + vec2(0.5)) / iResolution.yy;
    vec2 p = uv.xy * vec2(2.0) - vec2(1.0);
	fragColor = vec4(pow(Render(p), vec3(1.0 / 2.2)), 1.0);
}

