//#define TORUS_EMITER

// default
const float uAA = 0.01131;
const float uBB = 0.04;
const float uCC = 1.75;
//uniform sampler2D uCol;
const vec3 uColorBG = vec3(0.99999,0.999997,1);
const vec3 uColorLight = vec3(1,0.428571,0);
const vec2 uDiff = vec2(0.5068,0.7381);
const float uE = 1.75;
const float uLapCorner = 0.2;
const float uScale = 1.0013;
//uniform sampler2D uSim;

/* laplacian corner ratio */	#define lc uLapCorner
/* laplacian side ratio */ 		#define ls (1.0-uLapCorner)
/* key space */ 				#define kbd_space 32

vec4 get(sampler2D sam, vec2 g, vec2 s, float x, float y)
{
    g += vec2(x,y);
	return texture(sam, g / s);
}

vec4 frag(
    sampler2D sam, vec2 g, vec2 s, 
    int i, vec4 mo, sampler2D kbd, 
    float time, sampler2D noise)
{
	g -= s * 0.5;
	g *= uScale;
	g += s * 0.5;

    vec4 l 	= 	get(sam, g, s, -1. ,  0.);
	vec4 lt = 	get(sam, g, s, -1. ,  1.);
	vec4 t 	= 	get(sam, g, s,  0. ,  1.);
	vec4 rt = 	get(sam, g, s,  1. ,  1.);
	vec4 r 	= 	get(sam, g, s,  1. ,  0.);
	vec4 rb = 	get(sam, g, s,  1. , -1.);
	vec4 b 	= 	get(sam, g, s,  0. , -1.);
	vec4 lb = 	get(sam, g, s, -1. , -1.);
	vec4 c 	= 	get(sam, g, s,  0. ,  0.);
	vec4 lap = (l+t+r+b)/4.*ls + (lt+rt+rb+lb)/4.*lc - c; // laplacian

	// http://www.math.univ-toulouse.fr/~cbesse/teaching/M2/M2Mapi3/Projet.pdf
	// da = Du*lap + f(u,v);
	// db = Dv*lap + g(u,v);
	// GrayScott : Du!=0,Dv!=0
	// f(u,v) = F(1-u) - uv
	// g(u,v) = uv - (F+K)*v

	vec2 uv = g / s;

	float Du = uDiff.x;
	float Dv = uDiff.y;
	float re = c.x * c.y * c.y;
    c.x += Du * lap.x + uAA * (1.0 - c.x) - re;
	c.y += Dv * lap.y + re - (uAA + uBB) * c.y;

    float kbdValue = texelFetch(kbd, ivec2(kbd_space,1),0).x;

	vec2 p = (g-s*0.5)/s.y;
    float di = length(p)-0.45;
    float da = abs(di)-0.01;

    if (i < 1)	c = vec4(1,0,0,1);
	if (kbdValue > 0.) c = vec4(1,0,0,1);
	if (length(g - s * 0.5) < 5.) c = vec4(0,1,0,1);

#ifdef TORUS_EMITER
    if (da < 0.0 ) c = vec4(0,1,0,1);
#endif

	if (mo.z > 0.) 
	{
		if (length(g - mo.xy) < 5.) 
			c = vec4(0,1,0,1);
	}

	return vec4(clamp(c.xy, 0., 1e1), 0, 1);
}

