    // Init with no velocity and some pressure
    if (iFrame < 1) Q = vec4(.2);
    // At boundarys turn all kinetic energy into potential energy
    //if(U.x<3.||R.x-U.x<3.||U.y<3.||R.y-U.y<3.)Q = vec4(p(Q));
}

