        lerp = -cos(3.14 * lerp) * 0.5 + 0.5;
        float dtt = lerp * iTimeDelta * 20.0;
        lastColor = hsl2rgb(mod(iTime * 0.5, 1.0), 0.5, 0.5) * dtt +
            lastColor * (1.0 - dtt);
    }

    fragColor = vec4(lastColor,1.0);
}

