 ;var res=this.mRenderer.CreateShader(vsSource,fsSource)
 ;if(res.mResult==false)return res.mInfo
 ;if(this.mProgram!=null)this.mRenderer.DestroyShader(this.mProgram)
 ;this.mProgram=res
 ;this.mFrame=0
 ;return null;}

