 ;this.mHeaderLength=fsSource.split(/\r\n|\r|\n/).length
 ;fsSource+=shaderCode
 ;fsSource+=this.mImagePassFooter
 ;var res=this.mRenderer.CreateShader(vsSource,fsSource)
