buffer_a_code:
#define pi (acos(-1.))

vec2 rotate(vec2 a, float b)
{
    float c = cos(b);
    float s = sin(b);
    return vec2(
        a.x * c - a.y * s,
        a.x * s + a.y * c
    );
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

vec2 hash2( const float n ) {
	return fract(sin(vec2(n,n+1.))*vec2(43758.5453123));
}

int m;

// distance function
float scenediff(vec3 p)
{
    float d = 1e9;

    float tunnel = length(p.yx)-1.5;
    d=-tunnel;

    // ridges
    {
        vec3 q=p;
    	q.z=mod(q.z,.4)-.2;
    	d=min(d,sdTorus(q.xzy,vec2(1.51,.02)));
    }

    // floor
    float ground = p.y+1.;
    d=min(d,ground);

    // tiles
    {
        vec3 q=p;
        q.y+=.998;
        q.xz=rotate(q.xz,-pi*.25);
        q.xz=mod(q.xz,.1)-.05;
        d=max(d,.005-length(q.yx));
        d=max(d,.005-length(q.yz));
    }

    return d;
}

float scenelite(vec3 p)
{
    float d=1e9;

    // lamps
    {
        vec3 q=p;
        q.x=abs(q.x);
        q.xy+=vec2(-1.1,1);
        d=min(d,max(
            length(q.yx)-.05,
            abs(mod(q.z-1.,2.)-1.)-.01
        ));

        // minus lamps at grill
        d=max(d,1.-abs(p.z));
    }

    //lasers
    {
        vec3 q=p;
        q.xy=rotate(q.xy,-pi*.25);
        d = min(d,max(
            abs(q.z)-.1,
            abs(mod(q.y,.2)-.1)-.01
        ));
    }

    return d;
}

float scene(vec3 p)
{
    float diff=scenediff(p);
    float lite=scenelite(p);
    m=(lite<diff)?1:0;
    return min(lite,diff);
}

// ray bouncing function "borrowed" from I can't remember where
vec2 rv2;
vec3 B(vec3 n) {
    float theta = 2. * pi * rv2.x;
    float phi = acos(1. - 2. * rv2.y);
    float x = sin(phi) * cos(theta);
    float y = sin(phi) * sin(theta);
    float z = cos(phi);

    vec3 a = normalize(vec3(x,y,z));
    return dot(a,n)<0.?-a:a;
}


vec3 trace(vec3 cam, vec3 dir)
{
    vec3 accum = vec3(1);
    for(int bounce=0;bounce<4;++bounce)
    {
        float t=0.;
        float k;
        for(int i=0;i<100;++i)
        {
            k = scene(cam+dir*t);
            t += k;
            if (abs(k) < .001)
                break;
        }

		vec3 h = cam+dir*t;

        // if we hit something
        if(abs(k)<.001)
        {
            if (m==1){
                if (abs(h.z)<0.2){
                    // red laser grill
                	return vec3(1,0.15,0.15) * accum;
                }else{
                    // gold orbs
                    return vec3(5,2,.5) * accum;
                }
            }

			vec2 o = vec2(.001, 0);
			k=scene(h);
			vec3 n = normalize(vec3(
				scene(h+o.xyy) - k,
				scene(h+o.yxy) - k,
				scene(h+o.yyx) - k 
			));

            float roughness = .85;
            if (h.y<=-.999) {
                if (rv2.x<.95){
                	accum *= 0.125;
                	roughness = .01;
                }
            }

            cam = h+n*.001;
            vec3 mirror = reflect(dir,n);
            vec3 bounce = B(n);
            dir= normalize(mix(mirror,bounce,roughness));
            accum *= mix(1.,dot(dir,n),roughness);
        }
    }

    return vec3(0);
}

vec2 bokeh(){
	vec2 a=rv2;
    if(a.y>a.x)
        a=1.-a;
    a.y*=pi*2./a.x;
    return a.x*vec2(cos(a.y),sin(a.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // recover previous frame for iterative rendering
   	fragColor = texelFetch(iChannel0,ivec2(fragCoord),0);	

    // get UVs
    vec2 uv = fragCoord.xy/iResolution.xy-.5;

    // deliberately don't seed per-pixel
    float seed = iTime;
	rv2 = hash2(seed);

    // jitter for antialiasing
    uv += (rv2-.5)/iResolution.xy;

    // correct UVs for aspect ratio
    uv.x*=iResolution.x/iResolution.y;

    // careful seeding for blocky effect
    float blockSize=exp2(clamp(floor(length(uv)*9.),2.,9.));
    seed+=mod(floor(fragCoord.x/blockSize)+.01,1.34672);
    seed+=mod(floor(fragCoord.y/blockSize)+.01,1.72357);
    rv2=hash2(seed);

    // make a camera
    vec3 cam = vec3(0,0,-8.);
    vec3 dir = normalize(vec3(uv,1));

    // slight bokeh
    float ds=.005;
    vec2 bokehJitter=bokeh();
    cam.xy+=bokehJitter*ds;
    dir.xy-=bokehJitter*ds*dir.z/8.;

    // move the camera some more
    cam.y -=.25;
    dir.yz = rotate(dir.yz, .06);

    // compute the pixel color
	vec4 pixel = vec4(trace(cam,dir),1);

    // cap the sample-count
    if (iFrame < 512)
		fragColor += pixel;
}

