#define AA 2
#define PI 3.14159

struct Material
{
    vec3 	diffuseAlbedo;
    vec3 	specularAlbedo;
    float 	specularPower;
    float   bumpiness;
};

Material materials[] = Material[] 
(
    Material(vec3(0.0, 0.2, 0.2), vec3(0.3), 4.0, 0.2),
    Material(vec3(0.0, 0.1, 0.2), vec3(0.3), 4.0, 0.2),
    Material(vec3(0.0, 0.2, 0.1), vec3(0.3), 4.0, 0.2),
    Material(vec3(0.1, 0.2, 0.0), vec3(0.3), 8.0, 0.1),
    Material(vec3(0.2, 0.0, 0.2), vec3(0.3), 4.0, 0.15),
    Material(vec3(0.4, 0.3, 0.02), vec3(0.2), 1.0, 0.1),
    Material(vec3(0.2, 0.0, 0.2), vec3(0.2), 1.0, 0.1),
    Material(vec3(0.0, 0.1, 0.1), vec3(0.2), 1.0, 0.05)
);

vec2 rotate(vec2 v, float a) { return cos(a)*v + sin(a)*vec2(v.y,-v.x); }

//noise function from https://www.shadertoy.com/view/MsXGWr
vec3 texturize( sampler2D sa, vec3 p, vec3 n )
{
	vec3 x = texture( sa, p.yz ).xyz;
	vec3 y = texture( sa, p.zx ).xyz;
	vec3 z = texture( sa, p.xy ).xyz;
	return x*abs(n.x) + y*abs(n.y) + z*abs(n.z);
}

vec2 opUnion(vec2 a, vec2 b) { return a.x < b.x ? a : b; }

//distance function from https://iquilezles.org/articles/distfunctions
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

vec2 map(vec3 p)
{
    float objID = 0.0;
    vec2 res = vec2(sdRoundBox(p-vec3(0.0, 0.0, 0.0), vec3(0.7, 0.7, 0.7), 0.2), objID++);
    vec3 p2 = p - vec3(0.0, 1.3, 0.0); p2.xz = rotate(p2.xz, PI/6.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.3, 0.3, 0.3), 0.1), objID++));
    p2 = p - vec3(0.0, 2.2, 0.0); p2.xz = rotate(p2.xz, PI/9.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.5, 0.3, 0.5), 0.2), objID++));
    p2 = p - vec3(3.0, 0.3, 0.0); p2.xz = rotate(p2.xz, PI/9.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.7, 1.0, 0.7), 0.2), objID++));
    p2 = p - vec3(-3.0, -0.2, 0.5); p2.xz = rotate(p2.xz, PI/4.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.5, 0.5, 0.5), 0.2), objID++));
    p2 = p - vec3(-2.8, 1.3, 0.5); p2.xz = rotate(p2.xz, 0.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.6, 0.6, 0.6), 0.2), objID++));
    p2 = p - vec3(1.0, -0.4, 2.5); p2.xz = rotate(p2.xz, PI/6.0);
    res = opUnion(res, vec2(sdRoundBox(p2, vec3(0.4, 0.4, 0.4), 0.1), objID++));
    res = opUnion(res, vec2(p.y + 0.9, objID++));
    return res;
}

vec3 calculateTransmittance(vec3 ro, vec3 rd, float tmin, float tmax, vec3 atten)
{
    float thickness = 0.0;
    for (float t = tmin; t < tmax;)
    {
        float h = map(ro + t * rd).x;
        float dt = (abs(h) < 1e-5) ? 0.01 : min(tmax-t, abs(h));
        if (h < 0.0) thickness += dt; 
        t += dt;
    }

    return exp(-atten * thickness);
}

vec2 rayMarch(vec3 ro, vec3 rd, float tmin, float tmax)
{
	for (float t = tmin; t < tmax;)
    {
        vec3 pos = ro + t * rd;
        vec2 res = map(pos);

        float dist = res.x;
        if (dist < 0.001) return vec2(t, res.y);

        t += dist;
    }
    return vec2(1e6, -1.0);
}

vec3 calculateNormal(vec3 p)
{
    vec3 dt = vec3(0.001, 0.0, 0.0);
    return normalize( vec3 ( map(p+dt.xyy).x - map(p-dt.xyy).x,
    						 map(p+dt.yxy).x - map(p-dt.yxy).x,
                             map(p+dt.yyx).x - map(p-dt.yyx).x ) );
}

float calculateAO(vec3 pos, vec3 nor) //ambient occlusion
{
    float ao = 0.0, sca = 1.0;

    for (int i = 0; i < 5; ++i)
    {
    	float h = 0.01 + 0.11*float(i)/4.0;
        float d = map(pos+h*nor).x;
        ao += (h-d)*sca;
        sca *= 0.95;
    }

    return clamp(1.0-2.0*ao, 0.0, 1.0);
}

vec3 render(vec3 ro, vec3 rd)
{
	vec3 color = vec3(0.0);

    float an = iMouse.z>0.0 ? 10.0*(iMouse.y/iResolution.y-0.5) : (1.0-0.2*iTime);
    vec3 lightPos = vec3(3.0*cos(an), 1.3, 3.0*sin(an));
    vec3 lightColor = vec3(10.0, 8.0+sin(iTime)*6.0, 7.0+cos(iTime)*5.0);

    vec2 res = rayMarch(ro, rd, 0.01, 15.0);
    float t = res.x;
    float objID = res.y;

    //draw entities
    if (objID > -0.5)
    {
        Material mat = materials[int(objID)];
        vec3 pos = ro + t * rd;
        vec3 nor = calculateNormal(pos);
        vec3 bnor = -1.0 + 2.0 * texturize(iChannel0, 0.5*pos, nor);
		nor = normalize(nor + mat.bumpiness*bnor);

        vec3 lightDir = normalize(lightPos - pos);
        float lightDist = length(lightPos - pos);		
        float atten = 1.0 / (1.0 + 0.5 * lightDist * lightDist);
        vec3 light = atten * lightColor;
        light *= calculateTransmittance(pos+0.01*nor, lightDir, 0.01, min(lightDist, 20.0), vec3(1.0, 0.6, 0.5));

        float ao = calculateAO(pos, nor);
        color +=  light * mat.diffuseAlbedo;
        color += light * mat.specularAlbedo * pow(max(0.0, dot(reflect(lightDir,nor),rd)), mat.specularPower);
        color *= ao;
    }

    //draw lights
    {
        vec3 lv = lightPos - ro;
        float ll = length(lv);
        if (ll < t)
        {
            float angle = acos(clamp(dot(rd, lv/ll), 0.0, 1.0));
            color += 1e2*lightColor*pow((1.0-smoothstep(0.0, 0.8, angle*ll)), 32.0);    
        }
    }

    return color;
}

mat3 lookAt(vec3 eye, vec3 target, vec3 up)
{
    vec3 w = normalize(target - eye);
    vec3 u = normalize(cross(w, up));
    vec3 v = cross(u, w);
    return mat3(u, v, -w);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float an = iMouse.z>0.0 ? 10.0*(iMouse.x/iResolution.x-0.5) : 0.0;

	vec3 ro = 5.0*vec3(1.2*sin(an), 0.3, cos(an)+0.2);
    mat3 viewMat = lookAt(ro, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 color = vec3(0.0);
    for (int i = 0; i < AA; ++i) //super sampling
    {
        for (int k = 0; k < AA; ++k)
        {
            vec2 offset = vec2(float(i)+0.5,float(k)+0.5) / float(AA) - 0.5;
            vec2 uv = (2.0*(fragCoord+offset)-iResolution.xy)/iResolution.y;    
    	    vec3 rd = viewMat * normalize(vec3(uv, -2.0));

            color += render(ro, rd);
        }
    }

	color /= float(AA*AA);
	color = pow(color, vec3(0.4545));

    fragColor = vec4(color, 1.0);
}

vec3 bary(vec2 a, vec2 b, vec2 c, vec2 p) {
    vec2 v0 = b - a, v1 = c - a, v2 = p - a;
    float inv_denom = 1.0 / (v0.x * v1.y - v1.x * v0.y);
    float v = (v2.x * v1.y - v1.x * v2.y) * inv_denom;
    float w = (v0.x * v2.y - v2.x * v0.y) * inv_denom;
    float u = 1.0 - v - w;
    return abs(vec3(u,v,w));
}

vec4 thing(vec2 uv) {
    vec2 a = vec2( 0.0, 0.5);
    vec2 b = vec2(-0.5, 0.0);
    vec2 c = vec2( 0.0, -0.5);

    vec3 bcc = bary(a, b, c, uv);

    if(bcc.x + bcc.y + bcc.z <= 1.001) {
    	vec3 colA = vec3(0.7, 0.12, 0.05);
        vec3 colB = vec3(0.74, 0.57, 0.07);
        vec3 col = mix(colB, colA, bcc.y+0.1);
        return vec4(col, 1.0);
    } else {
        return vec4(0.0);
    }
}

vec4 mix_alpha(vec4 a, vec4 b) {
    return mix(a, b, b.a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    uv *= 1.3;

    vec4 col;

    if (iTime < 1.5) {
		col = vec4(mix(0.0, 0.76, clamp(iTime-0.5, 0.0, 1.0)));
    } else {
        col = thing(uv);

        col = mix_alpha(col, thing(-uv));

        float t = clamp(iTime - 1.9, 0.0, 1.0);
        float scale = mix(1.0, 2.2, t);
        vec2 off = vec2(-0.07, -0.43) * t;

        col = mix_alpha(col, thing(-uv * scale + off));

        col = mix_alpha(col, thing( uv * scale + off));

        col = mix_alpha(vec4(0.76, 0.76, 0.76, 1.0), col);

    }

    fragColor = vec4(col.rgb, 1.0);
}

