#define ss(d) smoothstep(.159155/p.y*b,-.159155/p.y*b, d)
#define sc(o) smoothstep(1.*b,-1.*b,p.y-r+o)

vec3 pal(float g)
{
	return pow(cos(.8-g-vec3(0,.25,.5) ), vec3(12) ); // https://www.shadertoy.com/view/WsffWB
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2.*fragCoord - iResolution.xy ) /iResolution.y;

    vec2 p = vec2(atan(uv.x,uv.y)/6.283185+.5, length(uv));

    float b = 2./iResolution.y;	// blur
    float r = .85;	// size
    float bg = .15; // background brightness


    // year
    float ty = fract(iDate.y/12. + iDate.z/30./12. + iDate.w/86400./30./12.);
    float dy = p.x-ty;
	float sy = ss(dy);
    float cy = sc(0.);
    vec3 year = vec3(max(sy*cy, bg*cy) * pal(1./7.));

    // month (inaccurate)
    float tm = fract(iDate.z/30. + iDate.w/86400./30.);
    float dm = p.x-tm;
	float sm = ss(dm);
    float cm = sc(1./8.);
    vec3 month = vec3(max(sm*cm, bg*cm) * pal(2./7.));

    // week (very inaccurate)
    float tw = fract(iDate.z/4.7 + iDate.w/86400./30.);
    float dw = p.x-tw;
	float sw = ss(dw);
    float cw = sc(2./8.);
    vec3 week = vec3(max(sw*cw, bg*cw) * pal(3./7.));

    // day
    float td = fract(iDate.w/86400.);
    float dd = p.x-td;
	float sd = ss(dd);
    float cd = sc(3./8.);
    vec3 day = vec3(max(sd*cd, bg*cd) * pal(4./7.));

    // hour
    float th = fract(iDate.w/3600.);
    float dh = p.x-th;
	float sh = ss(dh);
    float ch = sc(4./8.);
    vec3 hour = vec3(max(sh*ch, bg*ch) * pal(5./7.));

    // minute
    float tmi = fract(iDate.w/60.);
    float dmi = p.x-tmi;
	float smi = ss(dmi);
    float cmi = sc(5./8.);
    vec3 minute = vec3(max(smi*cmi, bg*cmi) * pal(6./7.));

    // second
    float ts = fract(iDate.w);
    float ds = p.x-ts;
	float ss = ss(ds);
    float cs = sc(6./8.);
    vec3 second = vec3(max(ss*cs, bg*cs) * pal(1.));


    vec3 col = mix(mix(mix(mix(mix(mix(
	year,
	month, cm),
	week, cw),
	day, cd),
	hour, ch),
	minute, cmi),
	second, cs);

    fragColor = vec4(col, 1.);
}

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURF_DIST 0.01
float capDist(vec3  p,vec3  a,vec3  b,float r){
vec3 ab = b-a;
vec3 ap = p-a;
float t = dot(ab,ap)/dot(ab,ab);
t = clamp(t,0.,1.);
vec3 c = a + t* ab;
return length(p-c) -r;
}
float torusDist(vec3 p ,vec3 o,vec2 r){
vec3 c = p-o;
vec2 x = vec2((length(c.xz)- r.x),c.y);
   return length(x) -r.y;
}
float getDist(vec3 p){
vec4 sphere = vec4(0,0.5,1.5,0.5);
 vec3 plane = vec3(0,0,0);
    float ds = length(p-sphere.xyz) - sphere.w;
    float td = torusDist(p,vec3(0,0.5,1.5),vec2(0.8,0.1));
    float dp = p.y - plane.y;
   return min(ds, min(dp,td));


}
float rayMarching(vec3 ro,vec3 rd){
    float dO= 0.0;
    for(int i = 0;i<MAX_STEPS;i++){
        vec3 p = ro + dO*rd;
        float ds = getDist(p);
        dO += ds;
        if(dO>MAX_DIST || ds < SURF_DIST){
            break;
        }
    }
    return dO;
}
vec3 getnormal(vec3 p){
    float d = getDist(p);
    vec2 e = vec2(0.01,0.0);
    vec3 n = d - vec3( getDist(p- e.xyy), 
                       getDist(p- e.yxy),
                       getDist(p- e.yxx));
    return normalize(n);
}
float getLight(vec3 p){
    vec3 lightO = vec3(0,4,2);
    lightO.xz += vec2(cos(iTime),sin(iTime))*2.0;
    vec3 n = getnormal(p);
    vec3 ray =  normalize(lightO-p);
    float light = clamp(dot(ray,n),0.0,1.0);
    float d = rayMarching(p +n*SURF_DIST*6.0,ray);
    if(d < length(lightO-p)) light *= 0.1;
    return light;
}
