vec2 reverbChannelB(float t) {
	vec2 s = vec2(0);

    vec2 reverb = vec2(0);
    float st = 0.001; float iters = 100.;
    for (float i = 0.; i < iters; i++) {
    	reverb += ((makeBells(t - i*st + random(i )*0.4, note(9.,1.), 0.4 ))/iters) *(1. - i/iters) ;
    	reverb += ((makeBells(t - i*st + sin(t*0.2)*0.01 + random(i )*0.4, note(16.,1.), 0.7))/iters) *(1. - i/iters) ;
    	reverb += ((makeBells(t - i*st - sin(t*0.3)*0.01 + random(i )*0.4, note(19.,1.), 0.7))/iters) *(1. - i/iters) ;
    	reverb += ((makeBells(t - i*st + 0.02 - sin(t*0.4)*0.01 + random(i )*0.4, note(12.,2.), 0.7))/iters) *(1. - i/iters) ;
    	reverb += ((makeBells(t - i*st - 0.01 + sin(t*0.5 + sin(0.2*t*note(16.,2.)*tau*0.5 + sin(0.3*t*note(16. + 7.,1.)))*0.13*sin(t*0.8))*0.01 + random(i )*0.4, note(16.,2.), 0.3))/iters) *(1. - i/iters) ;
    }

    s += ((makeBells(t , note(9.,1.), 0.4 ))/iters)*0.001;
    s += ((makeBells(t  , note(12.,2.), 0.1))/iters)*0.001;
    s += ((makeBells(t  , note(18.,2.), 0.5))/iters)*0.001;

    //s += makeBells(t, note(9.,1.))*0.01;
    s += reverb*5.5;
    //s *= 2.;
	return s;
}

vec2 droneChannel(float t){
	vec2 s = vec2(0);
	float iters = 20.;


    for(float i = 0.; i < iters; i++){
        float r = random(i);
        float rB = fract(r*124.52412);
    	s += reverbChannelB(t + r*0.1 + 0.02*r + sin(0.6*t*(1. - rB*0.05))*(0. + r*0.01));
