/* 
 * Another Wet Brick in the Wall
 */

// Antialiasing: Any number above 1 will make it look better, but slow.
#define AA 1


#define ZERO (min(iFrame,0))
#define MAX_STEPS			100
#define MAX_DIST			25.0
#define SURFACE_DIST		0.006


vec3 light_pos = vec3(-12.0, 18.5, -12.0);
vec3 ro = vec3(0.0, 2.8, -4.0);

float oTime;

vec2 closest(vec2 a, vec2 b)
{
    return a.x < b.x ? a : b;
}

vec2 wall(vec3 p)
{
    //float abs_noise_1 = abs(noise(p * 50.0));
    float abs_noise_1 = abs(fbm_2(p * 50.0));
    float abs_noise_2 = abs(noise(p * 5.0));

    // wall
    float f = dot(p, vec3(0.0, 0.0, -1.0));

   	vec2 wall = vec2(f - abs_noise_1 * 0.003 - abs_noise_2 * 0.013, 1.0);

    // bricks
    vec3 c = vec3(0.7, 0.65, 0.0);
    vec3 bp = p - vec3(0.0, 0.0, 0.004);
    vec3 bp1 = mod(bp + 0.5 * c, c) - 0.5 * c;
    bp -= vec3(0.35, 0.33, 0.0);
    vec3 bp2 = mod(bp + 0.5 * c, c) - 0.5 * c;

    float b = sdBox(bp1, vec3(0.288, 0.08, 0.03));
    b = min(b, sdBox(bp2, vec3(0.288, 0.09, 0.03)));
    vec2 bricks = vec2(b - abs_noise_1 * 0.005 - abs_noise_2 * 0.02 - .010, 2.0);

    return closest(wall, bricks);
}

vec2 map(vec3 p)
{
    vec3 wall2 = p;
    wall2.x -= 3.0;
    wall2.xz = wall2.zx;

    return closest(wall(p), wall(wall2));
}

vec3 calcNormal(vec3 p)
{
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(p+0.0001*e).x;
    }
    return normalize(n);
}

vec2 rayMarch(vec3 ro, vec3 rd)
{
    float t = 0.0;
    vec3 p;
    vec2 obj;
    for (int i = 0; i < MAX_STEPS; i++)
    {
        p = ro + t * rd;

        obj = map(p);

        if (obj.x < SURFACE_DIST || t > MAX_DIST) break;

        t += obj.x;
    }

    obj.x = t;
    return obj;
}

// Lighting
float ambientOcclusion(vec3 p, vec3 n)
{
	float stepSize = 0.002f;
	float t = stepSize;
	float oc = 0.0f;
	for(int i = 0; i < 10; ++i)
	{
		vec2 obj = map(p + n * t);
		oc += t - obj.x;
		t += pow(float(i), 2.2) * stepSize;
	}

	return 1.0 - clamp(oc * 0.2, 0.0, 1.0);
}

float getVisibility(vec3 p0, vec3 p1, float k)
{
	vec3 rd = normalize(p1 - p0);
	float t = 10.0f * SURFACE_DIST;
	float maxt = length(p1 - p0);
	float f = 1.0f;
	while(t < maxt || t < MAX_DIST)
	{
		vec2 o = map(p0 + rd * t);

		if(o.x < SURFACE_DIST)
			return 0.0f;

		f = min(f, k * o.x / t);

		t += o.x;
	}

	return f;
}

// Texturing
vec2 triplanar(vec3 p, vec3 normal)
{
    if (abs(dot(normal, vec3(0.0, 1.0, 0.0))) > .8)
    {
        return p.xz;
    }
    else if (abs(dot(normal, vec3(1.0, 0.0, 0.0))) > .8)
    {
        return p.yz;
    }
    else
    {
        return p.xy;
    }
}

vec3 textureWall(vec2 uv)
{
    vec2 iuv = floor(uv * 10.0);
    vec3 col = clamp(smoothstep(0.14, 0.65, noise(uv * 10.4)) + .9, 0.0, 1.0) * vec3(0.4);
    return col;
}

// Renderer
vec3 render(vec2 obj, vec3 p, vec3 rd, vec2 uv)
{
    vec3 col;

    vec3 normal = calcNormal(p);
    float fog;

    if (obj.x >= MAX_DIST)
    {
        col = vec3(1.0, 0.6, 0.4) * 2.0;
    }
    else
    {
        vec3 light_dir = normalize(light_pos - p);
        float diffuse = dot(light_dir, normal);

        float wet_mask = max(0.0, noise(p * 0.6));
        vec3 refd = reflect(rd, normal);
        float spec = pow(max(0.0, dot(refd, light_dir)), 15.0);

        float green_mask = max(0.0, noise(p * 2.1));

        fog = pow((obj.x / MAX_DIST), 2.3);
        float aa = ambientOcclusion(p, normal);
        float shadow = getVisibility(p, light_pos, 2.5);

        vec3 wp = p - 0.65 * fract(.5 * floor(2./0.65 * p.y+0.5));
        wp.xz = wp.xz-3.5*floor(1./0.7*wp.xz+.5);
        float bricknoise = ((noise(wp * 2.0)) * .5 + 0.7);
        float wallnoise = ((noise(p * 2.0)) * .2 + 0.7);

        if (obj.y >= 2.0) // Bricks
        {
            vec2 uv = triplanar(p, normal) * 3.0;

            uv = mod(uv * 0.02 + 0.5, 1.0);
            vec3 t = vec3(0.805, 0.194, 0.184) * bricknoise;

            col = t * aa * .5;
            col += diffuse * t * shadow;
            col -= max(0.0, 1.0 - wet_mask) * vec3(0.12);
            col += spec * vec3(1.4) * wet_mask;
            col += shadow * vec3(0.3, 0.22, 0.16) * 1.0;
            col *= aa;
        }
        else if (obj.y >= 1.0) // Walls
        {
            vec2 uv = triplanar(p, normal) * 3.0;

            uv = mod(uv * 0.02 + 0.5, 1.0);
            vec3 t = vec3(0.465, 0.454, 0.424) * wallnoise;

            col = t * aa * .9;
            col += diffuse * t * shadow;
            col -= max(0.0, 1.0 - wet_mask) * vec3(0.1);
            col += shadow * vec3(0.3, 0.22, 0.16) * 1.0;
            col *= aa;
        }
    }

    return mix(col, vec3(1.0, 0.6, 0.4) * 2.0, fog);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    oTime = iTime;

    float d = 1.0;
    float v = ((iMouse.x / iResolution.x) - .6) * -1.5 + sin(iTime * .1) * .4;
    vec3 ta = ro + vec3(sin(v) * d, clamp((iMouse.y / iResolution.y), -0.5, 0.5), cos(v) * d);

    ro.y += sin(iTime * .5) * .5;
    ta.y += sin(iTime * .3) * .2;


    vec3 tot = vec3(0.0);
#if AA>1
    for(int m=ZERO; m<AA; m++)
    for(int n=ZERO; n<AA; n++)
    {
        vec2 o = vec2(float(m), float(n)) / float(AA) - 0.5;
        vec2 uv = (2.0 * (fragCoord + o) - iResolution.xy) / iResolution.y;
#else    
    	vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
#endif       
        // Ray direction
        vec3 ww = normalize(ta - ro);
        vec3 uu = normalize(cross(ww, vec3(0.0, 1.0, 0.0)));
        vec3 vv = normalize(cross(uu, ww));

        vec3 rd = normalize(uv.x * uu + uv.y * vv + 2.3 * ww);

        // render	
        vec2 obj = rayMarch(ro, rd);
        vec3 p = ro + obj.x * rd;

   		vec3 col = render(obj, p, rd, uv);

        // gamma
    	col.xyz = pow( col.xyz, vec3(0.695) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif


    fragColor = vec4(tot,1.0);
}

