#define LINES 288.
#define ITER 6
#define DIF 0.002
#define GAP 0.007

vec3 gettex(vec2 p,float o){
    vec3 t=texture(iChannel0,p+vec2(o,0)).rgb;
    t+=hash(p+fract(iTime))*.2-.1;
    return clamp(t,0.,1.);
