    uv.y=floor(uv.y*LINES)/LINES;
    vec3 tx,yc,rgb=vec3(0);
    float o=-DIF*float(ITER)/2.;
    o+=sin(uv.y-iTime)>.999?sin(hash(uv.yy)*.04-.02):0.;
    for(int i=ZERO;i<ITER;i++){
	    tx=gettex(uv,o);yc.x=tx.r*.257+tx.g*.504+tx.b*.098+.0625;
	    tx=gettex(uv,o+GAP);yc.y=-tx.r*.148-tx.g*.291+tx.b*.439+.5;
	    tx=gettex(uv,o-GAP);yc.z=tx.r*.439-tx.g*.368-tx.b*.071+.5;
        yc.x+=sin(iTime*40.+uv.y*300.)*.03;
	    rgb+=vec3((yc.x-.0625)*1.164+(yc.z-.5)*1.596,
                (yc.x-.0625)*1.164-(yc.y-.5)*.391-(yc.z-.5)*.813,
                (yc.x-.0625)*1.164+(yc.y-.5)*2.018);
        o+=DIF;
    }
    rgb/=float(ITER);
    fragColor = vec4(rgb*sl,1);
}

