#define ITER 96
#define EPS 1e-3
#define NEAR 1.
#define FAR 100.

const vec2 e=vec2(EPS,-EPS);
vec3 h=vec3(.42);
float map(in vec3 p){
    p.xz=mod(p.xz,1.)-.5;p=abs(p)-h;
    return length(max(p,0.))+min(max(p.x,max(p.y,p.z)),0.)-.05;
}
float trace(in vec3 ro,in vec3 rd,out float c,out vec3 n){
    float t=NEAR,d;
    vec3 p;
	for(int i=ZERO;i<ITER;i++){
        p=ro+rd*t;h.y=fbm(p.xz)*3.;d=map(p);
        if(abs(d)<EPS||t>FAR)break;
        t+=step(d,.7)*d+d*.1;c+=1.;
    }
	n=normalize(e.xxx*map(p+e.xxx)+e.xyy*map(p+e.xyy)+e.yxy*map(p+e.yxy)+e.yyx*map(p+e.yyx));
    float w=(p.y<h.y-.2)&&(mod(p.y*4.,hash(floor(p.xz))*.5+.5)<hash(h.yy))?.7:1.;
    n*=w;
    c*=(w<1.)&&(hash(floor((p.xz)*12.))>.2)?.3:1.;
	return min(t,FAR);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y;
	vec3 n,ro=vec3(0,15,30),rd=vec3(uv,.8),ld=normalize(vec3(0.4,0.2,0.6));
    ro.xz*=rot(iTime*.2);
    rd.yz*=rot(-.4+sin(iTime*.25)*.3);
    rd.xy*=rot(sin(iTime*.14)*.3);
    rd.xz*=rot(iTime*.23);
	float c=0.,v=trace(ro,rd,c,n),s=pow(clamp(dot(n,ld),0.,1.),2.);
    s+=pow(clamp(dot(normalize(rd),ld),0.,1.),5.);
	fragColor=vec4(vec3(v/float(FAR),s,s+c/float(ITER)),1);
}

