//MUSIC:
//"Yokohama 2020"
//composed by Catzpaw 2020

//---Music Renderer https://www.shadertoy.com/view/tttGzB
// by Catzpaw 2019

//SETUP
const float base =432.;	//A tuning (Hz)
const float bpm  =96.;	//beats per minute
const float steps=480.;	//block length
const float start=2.;   //start count (beats)

//STATE
float gtime=0.;	//time
int   gchord=0;	//chord
float garp=0.;	//arpeggio pattern
float gdyn=1.;	//dynamics

//RANDOM ARP PATTERN GENERATOR
float hash(float s){s=fract(s*.13);s*=s+33.33;return fract(s+s*s);}
float pgen(float s,float r){
    int p=0;
    for(int i=0;i<16;i+=2){p+=int(hash(s)*r)<<i;s+=1.;}
    return float(p);
}

//AMP
//  vec3 p( drive, pressure, volume)
vec2 amp(vec2 i,vec3 p){
    vec2 v=pow(abs(i*p.x),vec2(1./p.y));
    return clamp(sign(i)*v,-1.,1.)*p.z;
}

//CHORD
//  global int gchord ...hex tetrad chord pattern
float chord(float n){ // n:place in gchord (0-3)
    float c=0.;
    if(n>2.)c+=float((gchord>>8)&15);
    if(n>1.)c+=float((gchord>>4)&15);
    if(n>0.)c+=float(gchord&15);
    return c; // return:relative note number to add to root note
}

//ARPEGGIATOR
//  vec3 p( pattern, step_length, pattern_steps)
//    arp_gate :p.pattern ...1bit pattern 1-16 steps 0:off 1:on
//    arp_chord:p.pattern ...2bit pattern 1-8 steps 0-3:note
float arp_gate(float t,vec3 p){
    float l=15.*p.y/bpm;
    float g=float((int(p.x)>>int(mod(t/l,p.z)))&1);
    return (g>0.)?mod(t,l):-1.; // return:gate time or note off (-1)
}
float arp_chord(float t,vec3 p){
    return chord(float((int(p.x)>>int(floor(mod(t/(15.*p.y/bpm),p.z))*2.))&3));
}

//FREQUENCY FROM MIDI NOTE NUMBER
//single note
float freq(float n){
    return pow(2.,(n-69.)/12.)*base*6.283;
}
//with tetrad chord
vec4 freq4(float n){
    vec4 r;
    r.x=freq(n);
    r.y=gchord>0?freq(n+chord(1.)):0.;
    r.z=gchord>16?freq(n+chord(2.)):0.;
    r.w=gchord>256?freq(n+chord(3.)):0.;
    return r;
}

//SOUND GENERATORS
//  sequence => note pattern => instrument => oscillator

//OSC
float osc_saw(float x,float v){return clamp(mod(x/6.283,1.)*2.-1.,-1.,1.)*v;}
float osc_sin(float x,float v){return clamp(sin(x)*v,-1.,1.);}
float osc_sqr(float x,float v){return clamp(sign(sin(x))*v,-1.,1.);}
float osc_noise(float x,float v){x=floor(x*1e3/v)*1e-3;return fract(sin(x*1717.17)*1313.13)*2.-1.;}
float osc_metal(float x,float v){return fract(sin(x*v*171.17)*313.13)*2.-1.;}

//ENVELOPE
const vec4 env0=vec4(0.00,.2,0.60,1.50); //piano
const vec4 env1=vec4(0.10,.4,0.50,0.50); //woodwind
const vec4 env2=vec4(0.20,.6,0.50,0.50); //string
const vec4 env3=vec4(0.00,.2,0.90,1.50); //synth
float env_d(float x,float d){return max(0.,1.-x/max(d,1e-4));}
float env_ad(float x,float a,float d){return min(x/max(a,1e-4),max(0.,1.-(x-a)/max(d,1e-4)));}
float env_adsr(float x,vec4 e,float g){return max(0.,min(1.,x/max(e.x,1e-4))-min(1.-e.z,max(x-e.x,0.)*(1.-e.z)/max(e.y,1e-4))-max(x-g,0.)*e.z/max(e.w,1e-4));}

//PERCUSSION INSTRUMENTS (time)
float bass_drum(float x){return osc_sin(x*freq(46./(1.+x*2.)),8.*env_d(x,.07));}
float snare_drum(float x){float f=freq(52./(1.+x*.02))*x;return (osc_noise(x,.05)*env_d(x,.3)*.5+osc_sin(f+osc_sin(f*1.92,.3),5.))*max(env_d(x,.1),env_d(x,.3)*.7);}
float hihat(float x,float d){return osc_metal(x,3.5)*env_d(x,d);}

//MELODIC INSTRUMENTS (freq)
//float vibe(float f){return osc_sin(f+osc_sin(f*8.+osc_sin(f*.02,1.),.3),1.5);}
//float harpsi(float f){return osc_saw(f*4.+osc_sin(f*18.,.4),1.3);}
//float organ(float f){return osc_sin(f,1.3)+osc_sin(f*.5,1.)+osc_sin(f*2.,1.)+osc_sin(f*4.,.5);}
//float reed(float f){return osc_sin(f,1.)*osc_sqr(f*3.,1.);}
float sawlead(float f){return osc_saw(f,1.)+osc_saw(f*2.01,1.);}
//float sqrlead(float f){return osc_sqr(f,.7)+osc_sqr(f*2.01,.7);}
//float synlead(float f){return osc_saw(f,1.)*osc_sqr(f*3.02,1.);}

//MELODIC INSTRUMENTS (freq,time)
//float piano(float f,float x){return osc_sin(f+osc_sin(f,env_d(x,.5))+osc_sin(f*3.,env_d(x,2.)*.8),1.);}
float epiano(float f,float x){return osc_sin(f+osc_sin(f*15.,.04)+osc_sin(f*.99,.4),1.);}
float bass(float f,float x){return osc_sin(f*.5+osc_sin(f*4.5,env_d(x,.1)*.5)+osc_sin(f*1.5,env_ad(x,.1,.2)*.3),2.)*env_d(x,.6);}
//float bell(float f,float x){return osc_sin(f+osc_sin(f*3.4,env_d(x,1.)*.2)+osc_sin(f*4.,.5),1.);}
//float guitar(float f,float x){return osc_sin(f+osc_sin(f*8.,env_d(x,.05)*.3)+osc_sin(f*5.,env_ad(x,.03,5.)*.3),2.);}

//NOTE OPERATORS
//float bup(float x){return min(x*(bpm/30.)-1.,0.);}
//float bdwn(float x){return max(1.-x*(bpm/30.),0.);}
//float pral(float x){float y=20./bpm;return ((x>y/2.)&&(x<y))?2.:0.;}
//float mord(float x){float y=20./bpm;return ((x>y/2.)&&(x<y))?-2.:0.;}
//float tril(float x){return sign(sin(3.14+x*bpm*6.28/15.))+1.;}

//NOTE PATTERNS
//P( note length, num of notes) ...pattern init
//T( position)                  ...set trigger (for percussion)
//N( position, note)            ...set note (for melody)
//NU( position, note)           ...set note with bend up
//ND( position, note)           ...set note with bend down
//NP( position, note)           ...set note with pral triller
//NM( position, note)           ...set note with mordent
//NT( position, note)           ...set note with trill
//X( position, macro)           ...execute macro (CHORD_mM7 etc.)
