//NOTE PATTERNS:percussion
vec2 bd(float t,float p){
    float g=arp_gate(t,vec3(p,1.,16.));
    return g<0.?vec2(0):amp(vec2(.6*bass_drum(g*1.05),.6*bass_drum(g*.95)),vec3(1,2.2,1));
}
vec2 sd(float t,float p){
    float g=arp_gate(t,vec3(p,1.,16.));
    return g<0.?vec2(0):vec2(.6*snare_drum(g*.995),.6*snare_drum(g*1.005));
}
vec2 hh(float t,float p1,float p2){
    float g=arp_gate(t,vec3(p1,1.,16.));
    vec2 r=g<0.?vec2(0):vec2(.2)*hihat(g,.08);
    g=arp_gate(t,vec3(p2,2.,8.));r+=g<0.?vec2(0):vec2(.15)*hihat(g,.3);
    return r;
}

//NOTE PATTERNS:epiano
vec2 ep(float x,float n){
    //epiano single note
	float f=freq(n)*x;
    return vec2(epiano(f*.995,x),epiano(f*1.005,x))*env_adsr(x,env0,.1)*gdyn;
}
vec2 epc(float x,float n){
    //epiano triad chord
	vec4 fl=freq4(n)*x,fr=fl*.995;fl*=1.005;
    return vec2(
        (epiano(fl.x,x)+epiano(fl.y,x)+epiano(fl.z,x)),
        (epiano(fr.x,x)+epiano(fr.y,x)+epiano(fr.z,x)))*env_adsr(x,env0,.1)*gdyn;
}
vec2 ep0(float t,float o){
