    P(1,32)
    N(0,HI Ds) N(1,R)
    N(3,As)    N(4,R)
    N(6,HI Cs) N(7,R)
    N(9,Gs)    N(10,R)
    N(12,B)    N(13,R)
    N(15,B)    N(16,R)
    N(17,B)    N(30,R)
    return z<0.?vec2(0):epc(x,z+o)*.3;
}
