//SEQUENCE
#define TRACK t=time;v=vec2(0);
#define   SEGNO(block,blocks)        if(t>float(block)*l){t=mod(t-float(block)*l,float(blocks)*l);
#define     REP(block,blocks,repend) if((t>float(block)*l)&&(t<=float(block)*l+float(repend)*l)){t=mod(t-float(block)*l,float(blocks)*l);
#define       SEQ(block,patterns)    if(t>float(block)*l){v=patterns;v*=d;}
#define     NEXT                     }
#define   DS                         }
#define END                          o+=v;
void sequence(float time,float l,float d,inout vec2 o){vec2 v=vec2(0);float t=time;

    DYN_mf

    TRACK //1 rhythm hh+bd
      SEGNO(0,16)
        SEQ(0.0,hh(t,4947.,0.))
        SEQ(1.0,hh(t,4947.,18440.))
        SEQ(1.5,hh(t,4947.,18440.)+bd(t,43305.))
      DS
    END

    TRACK //2 rhythm sd
      SEGNO(0,16)
        REP(0,4,4)
          SEQ(1.5,sd(t,61456.))
          SEQ(2.0,sd(t,4112.))
          SEQ(3.5,sd(t,36880.))
        NEXT
        REP(4,2,8)
          SEQ(0.0,sd(t,4112.))
          SEQ(1.5,sd(t,61456.))
        NEXT
        REP(8,4,16)
          SEQ(0.0,sd(t,4112.))
          SEQ(1.5,sd(t,36880.))
          SEQ(2.0,sd(t,4112.))
          SEQ(3.5,sd(t,61456.))
        NEXT
      DS
    END

    TRACK //3 epiano
      DYN_mf
      SEGNO(0,16)
        REP(0,2,12)
          CHORD_sus4on4
          SEQ(0,ep0(t,0.))
          SEQ(1,ep1(t,0.))
        NEXT
        REP(12,4,16)
          SEQ(0.0,ep2(t, 0.)+ep3(t, 0.)+ep4(t, 0.))
          SEQ(1.0,ep2(t,-2.)+ep3(t,-1.)+ep4(t, 0.))
          SEQ(1.5,ep2(t,-2.)+ep3(t,-4.)+ep4(t,-2.))
          SEQ(2.0,ep5(t, 0.)+ep3(t,-5.)+ep4(t,-3.))
          SEQ(3.0,ep6(t, 0.))
        NEXT
      DS
    END

    TRACK //4 bass
      DYN_mf
      SEGNO(0,16)
        REP(1,2,11)
          SEQ(0.0,bs0(t,0.))
          SEQ(0.5,bs1(t,2.))
          SEQ(1.0,bs0(t,0.))
          SEQ(1.5,bs1(t,0.))
        NEXT
        REP(12,4,16)
          gchord=0x322;
          SEQ(0.0,bs2(t,Cs))
          gchord=0x323;
          SEQ(0.5,bs2(t,C))
          gchord=0x432;
          SEQ(1.0,bs2(t,LO B))
          gchord=0x503;
          SEQ(1.5,bs2(t,LO As))
          SEQ(2.0,bs2(t,LO A))
          SEQ(3.0,bs3(t,0.))
        NEXT
      DS
    END

    TRACK //5 synth lead
      DYN_mp
      SEGNO(0,16)
        REP(0,6,12)
          REP(2,2,6)
            SEQ(0,ld0(t,0.))
            SEQ(1,ld1(t,0.))
          NEXT
        NEXT
        REP(12,4,16)
          SEQ(0,ld2(t,0.))
          SEQ(1,ld3(t,0.))
          SEQ(2,ld4(t,0.))
          SEQ(3,ld5(t,0.))
        NEXT
      DS
    END
}

//MASTER SECTION
#define MASTER_DRIVE    0.4
#define MASTER_PRESSURE 1.1
#define DELAY_REPEAT    4
#define DELAY_WET       0.15
#define DELAY_FEEDBACK  0.85
#define DELAY_TIME      0.05
vec2 mainSound( in int samp,float time){
    float l=steps/bpm,vol=1.,d=DELAY_WET,r=DELAY_TIME;vec2 o=vec2(0),v=vec2(0);
    if(time>170.){vol=(180.-min(time,180.))/10.;}
    time-=start*60./bpm;if(time<0.)return o;
    gtime=time;
    sequence(time,l,1.,o);
    for(int i=0;i<DELAY_REPEAT;i++){
        time-=r;gtime-=r;
        sequence(time,l,d,o);
        d*=DELAY_FEEDBACK;
        r+=DELAY_TIME;
    }
    return amp(o,vec3(MASTER_DRIVE,MASTER_PRESSURE,vol));
}

