float kcurve( float x, float k, float s )
{	
    float p = (1.-x)/(1.-k);
    float v = 4.-6.*abs(k-0.5);
    float h = pow( x/(k*p), k ) * p;

    return pow( h, v*s*s );

}

float scurve( float x, float k, float s )
{	
   float x1= 1.-fract((x-k)/(1.-k));
   float x2=fract(x/k);


 return x < k ? x2 : x1;//*k+x2*(1.-k);

}

