#define v3 vec3(0.0, 0.0, 1.0)
vec3 v2, w1, w2, w3;
vec3 vtxP, edgN, faceN;
float rad = 1.0;
void init(float m) {
    v2 = vec3(-0.5, sqrt(0.75 - pow(cos(PI / m), 2.0)), - cos(PI / m));
    float triProd = dot(cross(v1, v2), v3);
    w1 = normalize(cross(v3, v2) / triProd);
    w2 = normalize(cross(v1, v3) / triProd);
    w3 = normalize(cross(v2, v1) / triProd);
}
float edgSDF(vec3 p, float t) {// t:thickness
    return max(abs(dot(p - vtxP, faceN)), abs(dot(p - vtxP, edgN))) - t;
}
float vtxSDF(vec3 p, float t) {
    return length(p - vtxP) - t;
}
float faceSDF(vec3 p, float t) {
    return dot(p - vtxP, faceN) - t;
}
#define fold(w)p = p - 2.0 * max(dot(w, p), 0.0) * w;
float tetraSDF(vec3 p) {
    init(3.0);
    float scale = 2.0;
    float scalePow = 1.0;
    vtxP = rad * w1;
    for(int i = 0; i < 4; i ++ ) {
        fold(v1)fold(v3)fold(v2)fold(v3)fold(v1)fold(v2)
        p *= scale;
        scalePow *= scale;
        p -= (scale - 1.0) * vtxP;
    }  
    edgN = w2;
    faceN = w3;
    return edgSDF(p, 0.1) / scalePow;
}
float cubeSDF(vec3 p) {
    init(4.0);
    vtxP = rad * w3;
    float scale = 2.5;
    float scalePow = 1.0;
    for(int i = 0; i < 4; i ++ ) {
        fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)
        p *= scale;
        scalePow *= scale;
        p -= (scale - 1.0) * vtxP;
    }
    edgN = w2;
    faceN = w1;
    return faceSDF(p, 0.5) / scalePow;
}
float octaSDF(vec3 p) {
    init(4.0);
    vtxP = rad * w1;
    float scale = 2.0;
    float scalePow = 1.0;
    for(int i = 0; i < 3; i ++ ) {
        fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)
        p *= scale;
        scalePow *= scale;
        p -= (scale - 1.0) * vtxP;
    }
    edgN = w2;
    faceN = w3;
    return min(edgSDF(p, 0.02), vtxSDF(p, 0.3)) / scalePow;
}
float dodecaSDF(vec3 p) {
    init(5.0); 
    vtxP = rad * w1;
    float scale = PHI + 1.0;
    float scalePow = 1.0;
    for(int i = 0; i < 3; i ++ ) {
        fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)
        p *= scale;
        scalePow *= scale;
        p -= (scale - 1.0) * vtxP;
    }
    edgN = w2;
    faceN = w3;
    return faceSDF(p, 0.0) / scalePow;
}
float icosaSDF(vec3 p) {
    init(5.0);
    vtxP = rad * w3;
    float scale = PHI + 1.0;
    float scalePow = 1.0;
    for(int i = 0; i < 3; i ++ ) {
        fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)fold(v1)fold(v2)fold(v3)
        p *= scale;
        scalePow *= scale;
        p -= (scale - 1.0) * vtxP;
    }
    edgN = w2;
    faceN = w1;
    return edgSDF(p, 0.08) / scalePow;
}
mat3 euler(float h, float p, float r) {
    return mat3(
        cos(r) * cos(h) - sin(r) * sin(p) * sin(h), - sin(r) * cos(p), cos(r) * sin(h) + sin(r) * sin(p) * cos(h),
        sin(r) * cos(h) + cos(r) * sin(p) * sin(h), cos(r) * cos(p), sin(r) * sin(h) - cos(r) * sin(p) * cos(h),
        - cos(p) * sin(h), sin(p), cos(p) * cos(h)
    );
}
#define seq(v, s)d = mix(d, v, smoothstep(s - 0.3, s, t));
float sceneSDF(vec3 p) {
    float seqStep = 5.0;
    float t = abs(mod(iTime * 0.5, seqStep * 2.0) - seqStep);
    float d = tetraSDF(p);
    seq(cubeSDF(p), 1.0)
    seq(octaSDF(p), 2.0)
    seq(dodecaSDF(p), 3.0)
    seq(icosaSDF(p), 4.0)
    return d;
}
vec3 gradSDF(vec3 p) {
    float d = 0.0001;
    return normalize(vec3(
            sceneSDF(p + vec3(d, 0.0, 0.0)) - sceneSDF(p + vec3(-d, 0.0, 0.0)),
            sceneSDF(p + vec3(0.0, d, 0.0)) - sceneSDF(p + vec3(0.0, - d, 0.0)),
            sceneSDF(p + vec3(0.0, 0.0, d)) - sceneSDF(p + vec3(0.0, 0.0, - d))
        ));
    }
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // fragment position
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    //camera transform
    float t = 0.3 * iTime;
    mat3 g = euler(t, t, t);

    // camera
    vec3 cPos = g * vec3(0.0, 0.0, 2.0);
    vec3 cDir = g * vec3(0.0, - 0.0, - 1.0);
    vec3 cUp = g * vec3(0.0, 1.0, 0.0);
    float targetDepth = 1.0;
    vec3 cSide = cross(cDir, cUp);

    // light
    vec3 lightPos = g * vec3(0.0, 1.0, 1.0);

    // ray
    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir * targetDepth);
    vec3 rPos = cPos ;

    // marching loop
    for(int i = 0; i < 60; i ++ ) {
        rPos += sceneSDF(rPos) * ray;
    }

    // collision detection
    if (sceneSDF(rPos) < 0.001) {
        vec3 normal = gradSDF(rPos);
        float diff = clamp(dot(normalize(lightPos - rPos), normal), 0.01, 1.0);
        vec3 amb = vec3(0.0, 1.0, 0.0);
        fragColor = vec4(diff * amb, 1.0);
    } else {
        fragColor = vec4(1.0);
    }
}

#define PI 3.14159265359
mat3 rotX(float t) {
    return mat3(
        1.0, 0.0, 0.0,
        0.0, cos(t), sin(t),
        0.0, - sin(t), cos(t)
    );
}
mat3 rotY(float t) {
    return mat3(
        cos(t), 0.0, sin(t),
        0.0, 1.0, 0.0,
        - sin(t), 0.0, cos(t)
    );
}
mat3 rotZ(float t) {
    return mat3(
        cos(t), sin(t), 0.0,
        - sin(t), cos(t), 0.0,
        0.0, 0.0, 1.0
    );
}
float sphereSDF(vec3 p, vec3 cent, float rad) {
    return distance(p, cent) - rad;
}
float sceneSDF(vec3 p){
    float t = 0.5* iTime;
    p *= rotY(t) * rotX(t) * rotZ(t);
    float dist = 1.0;
    for (float i = 0.0; i < 6.0; i++){
        vec3 cent = vec3(cos(2.0 * PI * i / 6.0), sin(2.0 * PI * i / 6.0), 0.0);
        dist = min(dist, sphereSDF(p, cent, 0.2));
    }
    return mix(dist, sphereSDF(p, vec3(0.0), 1.), abs(mod(t, 2.0) - 1.0));
}
vec3 gradSDF(vec3 p) {
    float d = 0.0001;
    return normalize(vec3(
        sceneSDF(p + vec3(d, 0.0, 0.0)) - sceneSDF(p + vec3(-d, 0.0, 0.0)),
        sceneSDF(p + vec3(0.0, d, 0.0)) - sceneSDF(p + vec3(0.0, - d, 0.0)),
        sceneSDF(p + vec3(0.0, 0.0, d)) - sceneSDF(p + vec3(0.0, 0.0, - d))
    ));
}

