const float PI = 3.14159265359;
float kyoto(vec3 p){
    float scale = 1.0 / sqrt(2.0);
    return scale * (abs(p.x) + abs(p.y) + abs(p.z));
}
float shogi(vec3 p) {
    float scale = sqrt(2.0);
    return scale * max(max(abs(p.x), abs(p.y)), abs(p.z));
}
float euc(vec3 p){
    float scale = 1.0;
    return scale * length(p);
}
float dist(vec3 p){
    float t = abs(mod(0.5 * iTime, 4.0) - 2.0);
    return mix(
        mix(euc(p), shogi(p), clamp(t, 0.0, 1.0)),
        kyoto(p), clamp(t - 1.0, 0.0, 1.0)
    );
}
float ballSDF(vec3 p) {
    return dist(p) - 1.;
}
vec3 gradSDF(vec3 p) {
    float d = 0.0001;
    return normalize(vec3(
        ballSDF(p + vec3(d, 0.0, 0.0)) - ballSDF(p + vec3(-d, 0.0, 0.0)),
        ballSDF(p + vec3(0.0, d, 0.0)) - ballSDF(p + vec3(0.0, - d, 0.0)),
        ballSDF(p + vec3(0.0, 0.0, d)) - ballSDF(p + vec3(0.0, 0.0, - d))
    ));
}
mat3 euler(float h, float p, float r){
    return mat3(
        cos(r)*cos(h)-sin(r)*sin(p)*sin(h), -sin(r)*cos(p), cos(r)*sin(h)+sin(r)*sin(p)*cos(h),
        sin(r)*cos(h)+cos(r)*sin(p)*sin(h), cos(r)*cos(p), sin(r)*sin(h)-cos(r)*sin(p)*cos(h),
        -cos(p)*sin(h), sin(p), cos(p)*cos(h)
    ); 
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // fragment position
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    // transformation
    float t = 0.5 * iTime;
    mat3 g = euler(t, t, t);

    // camera
    vec3 cPos = g * vec3(0.0, 0.0, 2.0);
    vec3 cDir = g * vec3(0.0, 0.0, - 1.0);
    vec3 cUp = g * vec3(0.0, 1.0, 0.0);
    vec3 cSide = cross(cDir, cUp);
    float targetDepth = 1.0;

    // light
    vec3 lightPos = g *vec3(0.0, 1.0, 1.0);

    // ray
    vec3 ray = cSide * p.x + cUp * p.y + cDir * targetDepth;
    ray = ray / dist(ray);

    // marching loop
    vec3 rPos = cPos;
    for(int i = 0; i < 60; i++ ) {
        rPos += ray * ballSDF(rPos);
    }

    if (abs(ballSDF(rPos)) < 0.001) { //object
        vec3 normal = gradSDF(rPos);
        float diff = clamp(dot(normalize(lightPos - rPos), gradSDF(rPos)), 0.0, 1.0);
        vec3 col = vec3(0.0, 1.0, 0.0);
        fragColor = vec4(diff * col, 1.0);
    } else {    //background
        fragColor = vec4(1.0);
    }
}

