    float d = 0.0001;
    return normalize(vec3(
        sdf(p + vec3(d, 0.0, 0.0)) - sdf(p + vec3(-d, 0.0, 0.0)),
        sdf(p + vec3(0.0, d, 0.0)) - sdf(p + vec3(0.0, - d, 0.0)),
        sdf(p + vec3(0.0, 0.0, d)) - sdf(p + vec3(0.0, 0.0, - d))
    ));
}
