float rand21(vec2 uv){
    vec2 t = fract(vec2(uv.x*236.73,uv.y*678.47));
    float u = dot(uv*(t+143.66),t);
    return fract(t.x*u*345.87 + 365.22+t.y*u*475.57);
}
float rand21p(vec2 uv){
    vec2 p = floor(uv);
    vec2 fr = fract(uv);

    float grad = (
        rand21(p+vec2(0.,0.)) * (1.-fr.x) * (1.-fr.y) + 
        rand21(p+vec2(1.,0.)) * (fr.x) * (1.-fr.y) +
        rand21(p+vec2(0.,1.)) * (1.-fr.x) * (fr.y) +
        rand21(p+vec2(1.,1.)) * (fr.x) * (fr.y)
    );
    return grad;
}
float perlin(vec2 uv, float layers){
    float ret = 0.;
    for(float i=0.;i<layers;i++){
        ret += rand21p(uv*pow(2.,i)) / layers;
    }
    return ret;
}
float splatter(float time, vec2 uv){
    float n0 = perlin(uv+rand21(uv*dot(uv,vec2(time))),9.);
    return smoothstep(0.5,0.55,n0);
}
float ease(float time, vec2 uv){
    float cur = splatter(floor(time),uv);
    float next = splatter(floor(time)+1.,uv);
    float f = fract(time);
    return (1.-f)*cur + f*next;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5/iResolution.xy)/iResolution.y;

   	float stars = pow(rand21(uv),200.) * (ease(iTime*2.,uv)*.7+.3);

    float atmo = pow(smoothstep(4.,3.5,length(uv-vec2(.9,-3.6))),2.);
    vec3 atmo_col = vec3(.5,.5,1.);

    vec3 col = vec3(stars);
    col = max(atmo_col*atmo,stars);


    fragColor = vec4(col,1.0);
}

#define MAXD 100.

mat3 roty(float a) {
    return mat3(
    	cos(a), 0., sin(a),
        0., 1., 0.,
        -sin(a), 0., cos(a)
    );
}

float shape(vec3 p) {
    float sweepX = atan(p.z, p.y);
    float sweepY = atan(p.x, p.z);
    float sweepZ = atan(p.y, p.x);
    float period = floor(iMouse.y / iResolution.y * 10.);
    float radiusOffset = 
        (sin(sweepY * period))
        * (sin(sweepX * period))
        * (sin(sweepZ * period));

    return length(p) - (1. + radiusOffset*.2);
}

vec2 map(vec3 p) {
    // return a distance and a material id
    return vec2(shape(p), 1.);
}

vec3 norm(vec3 p) { 
    vec2 ep = vec2(0.001, 0.0);
    return normalize(map(p).r - vec3(
        map(p - ep.xyy).r,
        map(p - ep.yxy).r,
        map(p - ep.yyx).r
    ));
}

vec4 march(vec3 ro, vec3 rd) {
    float t = 0.;
    vec4 ret;
    for (int i = 0; i < 100; i++) {

        ret.xyz = ro + rd * t;
        vec2 d = map(ret.xyz);

        if (d.x < .001) {
        	ret.w = d.y;
            break;
        }

        t += d.x;

        if (t > MAXD) {
            break;
        }
    }
    return ret;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.5);

    vec3 ro = vec3(0., 0., 2.);
    vec3 rd = normalize(vec3(uv.xy, -1.));

    mat3 rot = roty((iMouse.x / iResolution.x - .5)*10.);
    ro *= rot;
    rd *= rot;

    vec4 p = march(ro, rd);

    if (p.w > 0.) {
        col = norm(p.xyz);
    }

    fragColor = vec4(col,1.0);
}

