#define MAX_DIST 100.
#define MIN_DIST 0.01
#define MAX_COUNT 100

float rand(vec2 uv)
{
    return fract(sin(uv.x * 1033.5 + uv.y) * 23930.58);
}
mat2 rotate(float ang){
    float ca = cos(ang);
    float sa = sin(ang);
    return mat2(ca, sa, -sa, ca);
}
float noise(vec2 uv){
 	vec2 xy = floor(uv);
    vec2 frac = fract(uv);
    frac = frac * frac * (3. - 2. * frac);

    vec2 bl = xy;
    vec2 tl = xy + vec2(0.,1.);
    vec2 br = xy + vec2(1.,0.);
    vec2 tr = xy + vec2(1.,1.);

    float a = rand(tl);
    float b = rand(tr);
    float c = rand(bl);
    float d = rand(br);

    float top = mix(a, b, frac.x);
    float bottom = mix(c, d, frac.x);

    return mix(bottom, top, frac.y);
}

float fbm(vec2 uv){
 	float res = 0.;
    float amp = .5;
    float freq = .5;

    for(int i = 0; i < 8; ++i)
    {
        uv *= rotate(80.);
        res += amp * noise(uv * freq + 3.0);
        amp *= 0.5;
        freq *= 2.0;
    }
    return res;
}


float getDist(vec3 p){   
    float disp = fbm(p.xz*0.08);
    //return disp;
    return (p.y+disp*50.)*.3;
}


vec3 normal(vec3 p){
	vec2 e = vec2(.001, 0.0);
    return normalize(vec3(
    	getDist(p + e.xyy) - getDist(p - e.xyy),
        getDist(p + e.yxy) - getDist(p - e.yxy),
        getDist(p + e.yyx) - getDist(p - e.yyx)
    ));
}


float rayMarch(vec3 ro, vec3 rd){
 	float R0 = 0.;
    for(int i = 0; i < MAX_COUNT; i++){
     	vec3 p = ro + R0 * rd;
        float d = getDist(p);
        R0 += d;
        if(d < MIN_DIST || R0 > MAX_DIST) break;
    }
    return R0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 mouse = iMouse.xy / iResolution.xy * 100.;
	vec3 ro = vec3(0., 2., -30.);
    ro.xz *= rotate(mouse.x);
    float zoom = .5;
    vec3 target = vec3(0.0);
    ro.z += iTime * 5.;
    target.z +=iTime * 5.;
    vec3 ww = normalize(target - ro);
    vec3 uu = normalize(cross(vec3(0., 1., 0.), ww));
    vec3 vv = normalize(cross(ww, uu));
    vec3 rd = normalize(uu * uv.x + vv * uv.y + ww * zoom);
    vec3 col = vec3(0.);
    float d0 = rayMarch(ro, rd);
    col = vec3(d0)/100.;
    fragColor = vec4(col,1.0);
}

// Procedural Music Sequencer
// by Hazel Quantock 2020
// This work is licensed under a Creative Commons Attribution-ShareAlike 4.0 International License. https://creativecommons.org/licenses/by-sa/4.0/


// Sound code is in ^Sound^ tab
// This just prints the text


// text rendering from: https://www.shadertoy.com/view/WdBXWd

// quality of font anti-aliasing - number of samples doubles with each level of quality
#define AA_QUALITY 3

// allow a little bleed between pixels - this looks more photographic, but blurrier
#define AA_ROUND false
#define AA_ROUND_RADIUS 0.7071


// Font Definitions
struct Typeface
{
    vec2 scale;
    vec2 padding;
    vec2 bias;
    float biasAngle;
    float threshold;
};

const Typeface Deco = Typeface( vec2(.6,.7), vec2(0), vec2(-.02,.025), 0., -.02 );
const Typeface Deco_Light = Typeface( vec2(.63,.7), vec2(-1,0), vec2(-.01,.04), 0., -.02 );

const Typeface Impactful = Typeface( vec2(.5,1), vec2(4,2), vec2(-.02,.01), 0., .01 );

const Typeface Elegant = Typeface( vec2(.6,.9), vec2(-2,0), vec2(.05,-.005), -1.04, -.022 );
const Typeface Elegant_Condensed = Typeface( vec2(.5,1.), vec2(0,1), vec2(.0,.1), -1., -.02 );

const Typeface Timely = Typeface( vec2(.6,.7), vec2(2,1), vec2(-.05,.0), -.5, -.02 );
const Typeface Timely_Light = Typeface( vec2(.6,.7), vec2(2,1), vec2(-.02,.02), -.5, -.01 );
const Typeface Timely_Heavy = Typeface( vec2(.8,.7), vec2(4,1), vec2(-.03,.02), 2.5, .01 );

const Typeface Neat = Typeface( vec2(.6,.7), vec2(-1,1), vec2(-.017,.02), -.3, -.02 );

const Typeface Comic = Typeface( vec2(.8,.7), vec2(2,1), vec2(-.05,.0), 1., -.02 );
const Typeface Comic_Title = Typeface( vec2(.6,.7), vec2(2,1), vec2(-.05,.0), 1., .0 );

const Typeface WildWest = Typeface( vec2(.9), vec2(2,0), vec2(-.05,.0), 0., -.02 );
const Typeface WildWest_Wide = Typeface( vec2(1,.7), vec2(0,2), vec2(.05,.0), 0., .02 );

const Typeface Gothic = Typeface( vec2(.9,.7), vec2(0,2), vec2(.05,.0), 1., .01 );
const Typeface Gothic_Light = Typeface( vec2(.9,.7), vec2(0,2), vec2(.05,.0), 1., -.01 );
const Typeface Gothic_Condensed = Typeface( vec2(.5,1.), vec2(0), vec2(.05,.0), -1.04, .02 );
const Typeface Gothic_Book = Typeface( vec2(.6,.7), vec2(0,2), vec2(.1,.0), 1., .0 );

const Typeface Haunted = Typeface( vec2(.75,.9), vec2(2,0), vec2(.0,.03), 0., .01 );
const Typeface Haunted_Condensed = Typeface( vec2(.5,1), vec2(4,0), vec2(-.02,.03), 0., .01 );
const Typeface Haunted_Script = Typeface( vec2(.6,.7), vec2(2,1), vec2(-.05,.05), .7, .0 );

// Round-tipped font (actually not round in close-up)
const Typeface Round = Typeface( vec2(1), vec2(0,6), vec2(-.02,-.02), 0., -.01 );
const Typeface Round_Bold = Typeface( vec2(1), vec2(4,6), vec2(-.04,-.04), 0., -.01 );



float RenderFont_NoAA
    (
        vec2 uv,
        Typeface font,
        uint text[10]
    )
{
    // each char is in a 64x64 space, trim off the white boundary
    vec2 charBottomLeft = vec2(18,3) - font.padding/2.;
    vec2 charDims = vec2(28,52) + font.padding;

    uv = uv/font.scale; //+vec2(iTime*200.,0);

    if ( uv.x < 0. || uv.y < .0 || uv.y >= charDims.y ) return 1.;

    uint index = uint(uv.x/charDims.x);
    ivec2 res = ivec2(iResolution.xy);
    uint index4 = index/4u;

    if ( index4 >= uint(text.length()) ) return 1.;

    uint char = ( text[index4] >> (8u*(index&3u)) )&0xffu;

    char = char^0xf0u; // flip the y coord

    uv.x = fract(uv.x/charDims.x)*charDims.x;
    uv += vec2(ivec2(char&0xfu,char>>4u)) * iChannelResolution[0].xy/16.;
    uv += charBottomLeft;

    vec4 v = texture( iChannel0, uv/iChannelResolution[0].xy ) -.5;

    vec2 bias = 2.*v.yz;
    bias = bias*cos(font.biasAngle) + bias.yx*sin(font.biasAngle)*vec2(1,-1);
    bias = pow(abs(bias),vec2(3));

    // view the SDF
//    float f = v.w + dot( font.bias, bias ) - font.threshold; return .5+.5*f/(abs(f)+.04);

    return step( font.threshold, v.w + dot( font.bias, bias ) );
}



// RenderFont
//
// Output:
//		A linear brightness value where 0 = text, 1 = background.
//		I recommend applying a gamma curve before displaying.
//
// uv
//		Relative to the bottom left of the line of text, most fonts are about 40 units tall
//
// font
//		One of the Typefaces defined earlier
//
// text
//		A line of text, encoded using my tool here: https://tekf.github.io/Misc-Tools/StringToHex.html
//		Each byte is 4-bit u, 4-bit v coord of a character in the font texture.
//
float RenderFont
    (
        vec2 uv,
        Typeface font,
		uint text[10]
    )
{
    const int numSamples = 1<<(AA_QUALITY);

    float sum = 0.;
    vec2 duvdx = dFdx(uv); // adjust anti aliasing without needing to know what scale we're working at!
    vec2 duvdy = dFdy(uv);

    // anti-alias the font
    // this would be more efficient run per-character inside the font render function
    // (the SDFs on some of the fonts are good enough that I could probably just soften the step function)
    for ( int i=0; i < numSamples; i++ )
    {
        // ideal 2D quasirandom sequence from http://extremelearning.com.au/unreasonable-effectiveness-of-quasirandom-sequences/
        uvec2 quasi2 = uvec2(0xC13FA9A9u,0x91E10DA5u);
        uint seed = uint(i);
        //seed += uint(iFrame*numSamples); // randomize per frame - causes shimmering
        //seed += uint(fragCoord.x)*quasi2.x+uint(fragCoord.y)*quasi2.y; // randomize per pixel - this looks bad at low sample counts (and at high counts it's less important)
        vec2 jitter = vec2( quasi2 * seed ) / exp2(32.);

        if ( AA_ROUND )
        {
            // circle of confusion slightly bigger than a pixel - should look more photographic
            jitter.x *= 6.283185;
            jitter = AA_ROUND_RADIUS*(1.-jitter.y*jitter.y)*vec2(cos(jitter.x),sin(jitter.x));
        }
        else
        {
            jitter -= .5;
        }

        sum += RenderFont_NoAA( uv + jitter.x*duvdx + jitter.y*duvdy, font, text );
    }

    return sum/float(numSamples);
}



void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-vec2(20,0))*400./iResolution.y;

    // Text encoded using my tool here: https://tekf.github.io/Misc-Tools/StringToHex.html
	float f = RenderFont( uv-vec2(135,160), Round_Bold, uint[]( 0x6f53200bU, 0x20646e75U, 0x0b206e4fU, 0x20202020u, 0x20202020u, 0x20202020u, 0x20202020u, 0x20202020u, 0x20202020u, 0x20202020u ) );

    if ( iTime > 5. )
    	f = min( f, RenderFont( uv-vec2(0,0), Neat,
            // It used to regenerate on rewind, but apparently iDate is now captured when the page loads
            //uint[]( 0x72502013U, 0x20737365U, 0x69776572U, 0x7420646eU, 0x6567206fU, 0x20612074U, 0x66666964U, 0x6e657265U, 0x75742074U, 0x2021656eU )
            uint[]( 0x65522020U, 0x64616f6cU, 0x67617020U, 0x6f742065U, 0x74656720U, 0x64206120U, 0x65666669U, 0x746e6572U, 0x6e757420U, 0x20202165U ) // length 40 = 0x28
        ) );

    // Output to screen
    fragColour.rgb = mix( vec3(0), vec3(1), f );

    // flash the screen when we start to give visual feedback when people press rewind
    fragColour.rgb *= smoothstep(0.,1.,iTime);

    fragColour.rgb = pow( fragColour.rgb, vec3(1./2.2) );
    fragColour.a = 1.;
}

