// Procedural Music Sequencer
// by Hazel Quantock 2020
// This work is licensed under a Creative Commons Attribution-ShareAlike 4.0 International License. https://creativecommons.org/licenses/by-sa/4.0/

// random hash from here: https://www.shadertoy.com/view/4dVBzz
#define M1 1597334677U     //1719413*929
#define M2 3812015801U     //140473*2467*11
#define M3 3299493293U     //467549*7057

#define F0 (1.0/float(0xffffffffU))

#define hash(n) n*(n^(n>>15))

#define coord1(p) ((p)*M1)
#define coord2(p) ((p).x*M1^(p).y*M2)
#define coord3(p) ((p).x*M1^(p).y*M2^(p).z*M3)

float hash1(uint n){return float(hash(n))*F0;}
vec2 hash2(uint n){return vec2(hash(n)*uvec2(0x1U,0x3fffU))*F0;}
vec3 hash3(uint n){return vec3(hash(n)*uvec3(0x1U,0x1ffU,0x3ffffU))*F0;}
vec4 hash4(uint n){return vec4(hash(n)*uvec4(0x1U,0x7fU,0x3fffU,0x1fffffU))*F0;}



int key;
float bps;

int majorScale( int index )
{
    return (index*12)/7; //0,2,4,6,7,9,11,12,14,16,18,19,...
}

int pentatonicScale( int index )
{
    return (index*12)/5; //0,2,4,7,9,12,14,16,19,...
}

float frequency( int note )
{
    return 440. * exp2( float(note + key) / 12. );
}

vec2 triangleWave( int note, float time )
{
    return fract(vec2(0,0)+frequency(note)*time)*2.-1.;
}

vec2 squareWave( int note, float time )
{
    return step(.5,fract(vec2(0,0)+frequency(note)*time))*2.-1.;
}

vec2 sineWave( int note, float time )
{
    return sin(6.283185*( vec2(0,0)+frequency(note)*time ));
}

void beat( out int i, out float f, float beatLength, float time )
{
    f = time*bps/beatLength;
    i = int(floor( f ));
    f -= float(i);
}

int invert( int note, int inversion )
{
    return ((note+48-inversion)%12)+inversion;
}

vec2 mainSound( in int samp, float time )
{
	// emulate lower sample rate
//	time = floor(time*5000.f)/5000.f;
	// emulate lower quality float (gradually loses precision over time)
//	float e = exp2( floor(log2(time)) - 16. ); // number of mantissa bits (roughly)
//	time = floor( time/e ) * e;

    // YES!! iDate.w changes every time and sound is regenerated on rewind!
    uint seed = coord1(uint(iDate.w));//uvec3(iDate));
    vec4 rand = hash4(seed);
    seed = coord1(seed);

    // todo: vary these on initialisation
    key = int(rand.x*12.999)-15;//-9; // relative to middle A
    int inversion = int(rand.y*12.999)-5;
    bps = 140./60.;
    ivec4 triads = ivec4(hash4(seed)*6.999);
    seed = coord1(seed);

    int beatsPerBar = 4*int(exp2(floor(rand.y*2.999)));
    int barsPerPhrase = 4; // this currently has to match the number of triads

    // timings
// this would be neater with a function taking number of beats returning int and float
// which would also work for arp
    int beat = int(floor(time*bps));
    float beatf = time*bps-float(beat);
    int bar = beat/beatsPerBar;
    float barf = (float(beat-bar*beatsPerBar)+beatf)/float(beatsPerBar);
    int phrase = bar/barsPerPhrase;
    float phrasef = (float(bar-phrase*barsPerPhrase)+barf)/float(barsPerPhrase);
// todo: have verse phrase, chorus phrase alternate a few times, then bridge phrase and final chorus phrase
// only difference between phrases should be random seed

    int barInPhrase = bar%barsPerPhrase;
    int beatInBar = beat%beatsPerBar;
    int beatInPhrase = beat%(beatsPerBar*barsPerPhrase);

    // note frequencies are: exp2(i/12.+octave)*baseFreq

    // 6 triads = alternate notes in the scale, starting on the first 6 notes

    // pick a triad for the bar
    int triadBaseNote = triads[barInPhrase];

    // invert the triads into the same range
    ivec3 triad =
        ivec3(
        	invert(majorScale(triadBaseNote),inversion),
        	invert(majorScale(triadBaseNote+2),inversion),
        	invert(majorScale(triadBaseNote+4),inversion)
        );

    // sort these notes! this is important for arp and bass
    if ( triad.x < triad.y )
    {
        if ( triad.y < triad.z ) triad.xyz = triad.xyz;
        else if ( triad.x < triad.z ) triad.xyz = triad.xzy;
            else triad.xyz = triad.zxy;
    }
    else
    {
        if ( triad.x < triad.z ) triad.xyz = triad.yxz;
        else if ( triad.y < triad.z ) triad.xyz = triad.yzx;
            else triad.xyz = triad.zyx;
    }

    vec2 f = vec2(0);

    // play the chord as pads
    float padAmp = .1*smoothstep(0.,.15,barf)*smoothstep(1.,.5,barf);
    f += (triangleWave(triad[0],time) + triangleWave(triad[1],time) + triangleWave(triad[2],time))*padAmp;

    // arpeggiator
    int arpPerBeat = int(floor(pow(rand.w,2.)*4.999))+2; // I thought 5 would sound bad but it's ok actually
    float arpf = time*bps * float(arpPerBeat);
    int arpb = int(floor(arpf));
    arpf -= float(arpb);

    // run through the notes of the triad
    vec4 randArp = hash4(seed);
    vec4 randArp2 = hash4(coord2(uvec2(seed,barInPhrase)));
    seed = coord1(seed);
    int range = int(randArp2.y*5.999+3.); // use arp2 so range changes from bar to bar, sounds more interesting
    int arpNote =
        randArp.z > .5 ?
        abs(((arpb+int(randArp.x*100.999))%(range*2))-(range-1)) // yoyo
    	: arpb%range; // ascending
    arpNote += int(randArp.w*5.999)-2;
    float arpAmp = .1*step(arpf,.5);//smoothstep(0.,.001,arpf)*smoothstep(.5,.499,arpf);//pow(1.-arpf,2.);

    int octFudge = 2; // mods go wrong on -ves
    arpNote += octFudge*3;
    f += squareWave(triad[arpNote%3] + 12*(arpNote/3 - octFudge),time)*arpAmp;


    // bass line - play the bottom note of the chord in a random rhythm
	// it's ok but the randomness is a bit too random
    if ( hash1(coord2(uvec2(seed,barInPhrase)))*.9+.1 > hash1(coord2(uvec2(beatInBar,seed)))*2.*float(beatInBar)/float(beatsPerBar) )
    {
        float bassAmp = .25*smoothstep(.0,.001,beatf)*pow(1.-beatf,1.);
        int note = triad[0];
        int vary = int(20.999*hash1(coord2(uvec2(beatInPhrase,seed))));
        // I thought this would sound good but it really doesn't
        //if ( vary < 5 ) note = invert( pentatonicScale(vary), inversion-6 );
        if ( vary < 5 ) note = triad[2]-12;
        if ( vary < 2 ) note = triad[1];
        f += mix(
            	sineWave(note-12,time)*3.,
            	triangleWave(note-24,time),
            //mix(0.,.5,pow(1.-beatf,2.))) // quite a nice pluck effect
            mix(0.,.3+.4*hash1(coord1(uint(beat))),pow(1.-beatf,3.))) // add a little randomness to the "harshness" of the pluck
            * bassAmp;
    }


    // todo: Melody
    // more complicated rhyhm than bass
    // pick notes randomly from the pentatonic scale for the melody
    // but start and end each phrase with a note from the triad


    return f*.3;
}

